import React, { useState, useEffect } from 'react';
import axios from 'axios';

function VerticalNavBar({ onFilterChange, onYearChange, toggleCompanyList, showCompanyList }) {
    const [years, setYears] = useState([]);
    const [selectedYear, setSelectedYear] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async() => {
            try {
                const response = await axios.get('/api/filters');
                if (response.data) {
                    const { years } = response.data;
                    if (Array.isArray(years)) setYears(years);
                }
                setLoading(false);
            } catch (error) {
                console.error('Error fetching filter data:', error);
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    const handleYearChange = (event) => {
        const selectedYear = event.target.value;
        setSelectedYear(selectedYear);
        onYearChange(selectedYear); // Pass year to the parent
    };

    const handleCompaniesVisitedClick = () => {
        if (!selectedYear) {
            alert('Please select a year before viewing the companies list.');
            return;
        }
        toggleCompanyList();
    };

    if (loading) {
        return <div > Loading filters... < /div>;
    }

    return ( <
        div className = "vertical-nav" >
        <
        button onClick = {
            () => onFilterChange('All Students') } > All Students < /button> <
        button onClick = {
            () => onFilterChange('Placed') } > Placed Students < /button> <
        button onClick = {
            () => onFilterChange('Not placed') } > Not Placed Students < /button>

        { /* Year Dropdown */ } <
        select onChange = { handleYearChange } >
        <
        option value = "" > Select Year < /option> {
            years.length > 0 ? (
                years.map((year, index) => ( <
                    option key = { index }
                    value = { year } >
                    Year { year } <
                    /option>
                ))
            ) : ( <
                option disabled > No years available < /option>
            )
        } <
        /select>

        { /* Companies Visited Button */ } <
        button onClick = { handleCompaniesVisitedClick } > { showCompanyList ? 'Hide Companies' : 'Companies Visited' } <
        /button> <
        /div>
    );
}

export default VerticalNavBar;
