import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './VerticalNavBar.css';

function VerticalNavBar({
    onFilterChange,
    toggleCompanyList,
    showCompanyList, //filter, year, searchTerm, schoolFilter, typeFilter
    schoolFilter,
    yearFilter,
    typeFilter,
    setSchoolFilter,
    setYearFilter,
    setTypeFilter,
}) {
    const [schools, setSchools] = useState([]);
    const [types, setTypes] = useState([]);
    const [years, setYears] = useState([]);

    // Fetch schools, types, and years from the backend
    useEffect(() => {
        const fetchFilters = async() => {
            try {
                const response = await axios.get('/api/filters');
                setSchools(response.data.schools || []);
                setTypes(response.data.types || []);
                setYears(response.data.years || []);
            } catch (error) {
                console.error('Error fetching filters:', error);
            }
        };

        fetchFilters();
    }, []);

    const handleFilterChange = (filterType, value) => {
        if (filterType === 'School') {
            setSchoolFilter(value);
        } else if (filterType === 'Year') {
            setYearFilter(value);
        } else if (filterType === 'Type') {
            setTypeFilter(value);
        }
    };

    return ( <
            div className = "vertical-nav" > { /* Filter buttons */ } <
            button onClick = {
                () => onFilterChange('All Students')
            } > All Students < /button> <
            button onClick = {
                () => onFilterChange('Placed')
            } > Placed Students < /button> <
            button onClick = {
                () => onFilterChange('Not Placed')
            } > Not Placed Students < /button>

            { /* School filter */ } <
            select value = { schoolFilter || '' }
            onChange = {
                (e) => handleFilterChange('School', e.target.value)
            } >
            <
            option value = "" > Select School < /option> {
            schools.length > 0 ? (
                schools.map((school) => ( <
                    option key = { school }
                    value = { school } > { school } <
                    /option>
                ))
            ) : ( <
                option value = "" > Loading schools... < /option>
            )
        } <
        /select>

    { /* Year filter */ } <
    select value = { yearFilter || '' }
    onChange = {
            (e) => handleFilterChange('Year', e.target.value)
        } >
        <
        option value = "" > Select Year < /option> {
    years.length > 0 ? (
        years.map((year) => ( <
            option key = { year }
            value = { year } >
            Year { year } <
            /option>
        ))
    ) : ( <
        option value = "" > Loading years... < /option>
    )
} <
/select>

{ /* Type filter */ } <
select value = { typeFilter || '' }
onChange = {
        (e) => handleFilterChange('Type', e.target.value)
    } >
    <
    option value = "" > Select Type < /option> {
types.length > 0 ? (
    types.map((type) => ( <
        option key = { type }
        value = { type } > { type } <
        /option>
    ))
) : ( <
    option value = "" > Loading types... < /option>
)
} <
/select>

{ /* Companies visited toggle */ } <
button onClick = { toggleCompanyList } > { showCompanyList ? 'Hide Companies' : 'Companies Visited' } <
    /button>

{ /* Job Notifications Link */ } <
Link to = "/job-notifications"
className = "nav-link" >
    Job Notifications <
    /Link> < /
div >
);
}

export default VerticalNavBar;
