import React from 'react';
import './StudentCard.css';

function StudentCard({ student }) {
    console.log('Student data in StudentCard:', student);

    if (!student) {
        return <div > Invalid student data < /div>;
    }

    const companies = student.Companies || [];
    const statuses = student.Statuses || [];

    // Get the most recent status (assuming the last status is the most relevant)
    const currentStatus = statuses[statuses.length - 1];

    // Determine the background color based on application status
    const getStatusColor = (status) => {
        switch (status) {
            case 'Placed':
                return { background: '#d4edda', border: 'green' }; // Light green for placed
            case 'Not placed':
                return { background: '#f8d7da', border: 'red' }; // Light red for not placed
            case 'Shortlisted':
                return { background: '#fff3cd', border: '#ffeeba' }; // Light orange for shortlisted
            case 'Applied':
                return { background: '#f8f9fa', border: '#dee2e6' }; // Light grey for applied
            default:
                return { background: '#ffffff', border: '#dee2e6' }; // Default to white
        }
    };

    const { background, border } = getStatusColor(currentStatus); // Get the color for the current status

    return ( <
        div className = "student-card"
        style = {
            { backgroundColor: background, borderColor: border }
        } // Set background color and border color
        >
        <
        h3 > { student.Name } < /h3> <
        p > Reg No: { student.Reg_No } < /p> <
        p > Year: { student.Year } < /p> <
        div className = "student-companies" >
        <
        ul > {
            companies.map((company, index) => ( <
                li key = { index } >
                <
                strong > { company } < /strong>: {statuses[index]} < /
                li >
            ))
        } <
        /ul> < /
        div > <
        /div>
    );
}

export default StudentCard;
