import React from 'react';
import './StudentDetailModal.css'; // Ensure this file contains necessary styles for the modal

const statusColors = {
    Applied: 'white',
    Shortlisted: 'orange',
    'Not Placed': 'red',
    Placed: 'green',
};

const StudentDetailModal = ({ student, onClose }) => {
    return ( <
        div className = "modal-overlay" >
        <
        div className = "modal-content" >
        <
        h1 > Student Details < /h1> <
        p > < strong > Name: < /strong> {student.Name}</p >
        <
        p > < strong > Reg No: < /strong> {student.Reg_No}</p >
        <
        p > < strong > Year: < /strong> {student.Year}</p >

        <
        h3 > Applications: < /h3> {
            student.Applications && student.Applications.length > 0 ? ( <
                div className = "applications-list" > {
                    student.Applications.map((app, index) => ( <
                        p key = { index }
                        style = {
                            {
                                backgroundColor: statusColors[app.ApplicationStatus],
                                padding: '5px',
                                borderRadius: '5px',
                                margin: '5px 0'
                            }
                        } >
                        <
                        strong > Company: < /strong> {app.Company}  <
                        span > ({ app.ApplicationStatus }) < /span> <
                        /p>
                    ))
                } <
                /div>
            ) : ( <
                p > No applications found
                for this student. < /p>
            )
        }

        <
        button className = "close-button"
        onClick = { onClose } > Close < /button> <
        /div> <
        /div>
    );
};

export default StudentDetailModal;
