import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSquarePen, faTrashCan, faRectangleXmark } from '@fortawesome/free-solid-svg-icons';

const NotificationManager = () => {
    const [newNotification, setNewNotification] = useState({
        company: '',
        date: '',
        category: '',
        eligibleBranches: '',
        eligibleCriteria: '',
        ctc: '',
        website: '',
        jobLocation: '',
        description: '',
    });
    const [notifications, setNotifications] = useState([]);
    const [showDetailModal, setShowDetailModal] = useState(false);
    const [selectedNotification, setSelectedNotification] = useState(null);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewNotification((prev) => ({...prev, [name]: value }));
    };

    const handleAddNotification = () => {
        if (newNotification.company) {
            setNotifications((prev) => [...prev, {...newNotification, id: Date.now() }]);
            setNewNotification({
                company: '',
                date: '',
                category: '',
                eligibleBranches: '',
                eligibleCriteria: '',
                ctc: '',
                website: '',
                jobLocation: '',
                description: '',
            });
        }
    };

    const handleRemoveNotification = (id) => {
        setNotifications((prev) => prev.filter(notification => notification.id !== id));
    };

    const handleCardClick = (notification) => {
        setSelectedNotification(notification);
        setShowDetailModal(true);
    };

    const closeDetailModal = () => {
        setShowDetailModal(false);
        setSelectedNotification(null);
    };

    return ( <
        div >
        <
        div className = "notification-form" >
        <
        input type = "text"
        name = "company"
        placeholder = "Company Name"
        value = { newNotification.company }
        onChange = { handleInputChange }
        /> <
        input type = "date"
        name = "date"
        value = { newNotification.date }
        onChange = { handleInputChange }
        /> <
        input type = "text"
        name = "category"
        placeholder = "Category"
        value = { newNotification.category }
        onChange = { handleInputChange }
        /> <
        button onClick = { handleAddNotification } > Add Notification < /button> <
        /div>

        <
        div className = "notification-list" > {
            notifications.map(notification => ( <
                div key = { notification.id }
                className = "job-card"
                onClick = {
                    () => handleCardClick(notification) } >
                <
                button className = "remove-buttonn"
                onClick = {
                    (e) => {
                        e.stopPropagation();
                        handleRemoveNotification(notification.id);
                    }
                } >
                <
                FontAwesomeIcon icon = { faTrashCan }
                /> <
                /button> <
                h3 > { notification.company || 'Unknown Company' } < /h3> <
                p > Date of Visit: { notification.date || 'No Date Provided' } < /p> <
                p > Category: { notification.category || 'No Category' } < /p> <
                /div>
            ))
        } <
        /div>

        {
            showDetailModal && selectedNotification && ( <
                div className = "modal" >
                <
                div className = "modal-content" >
                <
                button className = "close-icon"
                onClick = { closeDetailModal } >
                <
                FontAwesomeIcon icon = { faRectangleXmark }
                /> <
                /button> <
                h2 > { selectedNotification.company || 'Unknown Company' } < /h2> <
                p > < strong > Date of Visit: < /strong> {selectedNotification.date || 'No Date Provided'}</p >
                <
                p > < strong > Category: < /strong> {selectedNotification.category || 'No Category'}</p >
                <
                p > < strong > Eligible Branches: < /strong> {selectedNotification.eligibleBranches || 'No Branches'}</p >
                <
                p > < strong > Eligible Criteria: < /strong> {selectedNotification.eligibleCriteria || 'No Criteria'}</p >
                <
                p > < strong > CTC: < /strong> {selectedNotification.ctc || 'No CTC'}</p >
                <
                p > < strong > Website: < /strong> <a href={selectedNotification.website || '#'} target="_blank" rel="noopener noreferrer">{selectedNotification.website || 'No Website'}</a > < /p> <
                p > < strong > Job Location: < /strong> {selectedNotification.jobLocation || 'No Location'}</p >
                <
                p > < strong > Description: < /strong> {selectedNotification.description || 'No Description'}</p >
                <
                /div> <
                /div>
            )
        } <
        /div>
    );
};

export default NotificationManager;
