// MoreData.js
import React, { useState, useEffect } from 'react';
import { Bar } from 'react-chartjs-2';
import './MoreData.css'; // Import the CSS file for styling
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js';
import axios from 'axios'; // Make sure you have axios installed

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const MoreData = () => {
    const [halfDecades, setHalfDecades] = useState([]);
    const [firstHalfDecade, setFirstHalfDecade] = useState('');
    const [secondHalfDecade, setSecondHalfDecade] = useState('');
    const [data, setData] = useState(null);

    useEffect(() => {
        const fetchUniqueYears = async() => {
            try {
                const response = await axios.get('http://localhost:1000/api/students/unique-years'); // Update with your backend URL
                const years = response.data;
                const halfDecadesArray = generateHalfDecades(years);
                setHalfDecades(halfDecadesArray);
            } catch (error) {
                console.error('Error fetching unique years:', error);
            }
        };

        fetchUniqueYears();
    }, []);

    const generateHalfDecades = (years) => {
        const uniqueYears = Array.from(new Set(years)).sort((a, b) => a - b);
        const halfDecadesArray = [];

        for (let i = 0; i < uniqueYears.length; i += 5) {
            if (i + 4 < uniqueYears.length) {
                halfDecadesArray.push(`${uniqueYears[i]}-${uniqueYears[i + 4]}`);
            }
        }
        return halfDecadesArray;
    };

    const handleCompare = () => {
        // Simulated data fetch based on selected half-decades
        // This should ideally be another API call that fetches the data for the selected half-decades
        const comparisonData = {
            '2010-2014': { placed: 200, total: 500 },
            '2015-2019': { placed: 300, total: 600 },
            '2020-2024': { placed: 400, total: 700 },
        };

        if (firstHalfDecade && secondHalfDecade) {
            const firstData = comparisonData[firstHalfDecade];
            const secondData = comparisonData[secondHalfDecade];
            setData({
                first: {
                    decade: firstHalfDecade,
                    ...firstData,
                },
                second: {
                    decade: secondHalfDecade,
                    ...secondData,
                },
            });
        }
    };

    const getChartData = () => {
        return {
            labels: [data.first.decade, data.second.decade],
            datasets: [{
                    label: 'Placed',
                    data: [data.first.placed, data.second.placed],
                    backgroundColor: 'rgba(75, 192, 192, 0.5)',
                },
                {
                    label: 'Total',
                    data: [data.first.total, data.second.total],
                    backgroundColor: 'rgba(153, 102, 255, 0.5)',
                },
            ],
        };
    };

    return ( <
        div className = "more-data-page" >
        <
        h2 > Compare Placement Statistics < /h2> <
        div className = "selectors" >
        <
        select value = { firstHalfDecade }
        onChange = {
            (e) => setFirstHalfDecade(e.target.value) } >
        <
        option value = "" > Select First Half - Decade < /option> {
            halfDecades.map((hd) => ( <
                option key = { hd }
                value = { hd } > { hd } <
                /option>
            ))
        } <
        /select> <
        select value = { secondHalfDecade }
        onChange = {
            (e) => setSecondHalfDecade(e.target.value) } >
        <
        option value = "" > Select Second Half - Decade < /option> {
            halfDecades.map((hd) => ( <
                option key = { hd }
                value = { hd } > { hd } <
                /option>
            ))
        } <
        /select> <
        button onClick = { handleCompare } > Compare < /button> <
        /div>

        {
            data && ( <
                div className = "results" >
                <
                h3 > Comparison Results < /h3> <
                div className = "result-item" >
                <
                h4 > { data.first.decade } < /h4> <
                p > Placed: { data.first.placed } < /p> <
                p > Total: { data.first.total } < /p> <
                p > Placement Rate: {
                    ((data.first.placed / data.first.total) * 100).toFixed(2) } % < /p> <
                /div> <
                div className = "result-item" >
                <
                h4 > { data.second.decade } < /h4> <
                p > Placed: { data.second.placed } < /p> <
                p > Total: { data.second.total } < /p> <
                p > Placement Rate: {
                    ((data.second.placed / data.second.total) * 100).toFixed(2) } % < /p> <
                /div>

                { /* Render the chart */ } <
                Bar data = { getChartData() }
                /> <
                /div>
            )
        } <
        /div>
    );
};

export default MoreData;
