import React, { useState, useEffect } from 'react';
import { fetchStudents } from './path-to-api'; // Correct the path to where the API is located

function VerticalNavBar({ setStudents }) {
    const [filter, setFilter] = useState('all');
    const [year, setYear] = useState('');
    const [searchTerm, setSearchTerm] = useState('');
    const [school, setSchoolFilter] = useState('');
    const [type, setTypeFilter] = useState('');

    // Fetch students when filters change
    useEffect(() => {
        const fetchData = async() => {
            try {
                const studentsData = await fetchStudents(filter, year, searchTerm, school, type);
                setStudents(studentsData); // Update the students list
            } catch (error) {
                console.error('Error fetching students:', error);
            }
        };

        fetchData();
    }, [filter, year, searchTerm, school, type, setStudents]); // Dependency array with filters

    return ( <
        div >
        <
        button onClick = {
            () => setFilter('all') } > All Students < /button> <
        button onClick = {
            () => setFilter('Placed') } > Placed Students < /button> <
        button onClick = {
            () => setFilter('Not Placed') } > Not Placed Students < /button>

        <
        input type = "text"
        placeholder = "Search Students"
        value = { searchTerm }
        onChange = {
            (e) => setSearchTerm(e.target.value) }
        /> <
        /div>
    );
}

export default VerticalNavBar;
