import React, { useState, useEffect } from 'react';
import StudentCard from './StudentCard';
import StudentDetailModal from './StudentDetailModal';
import './StudentList.css';
import { fetchStudents } from '../api/studentAPI';
import { groupByRegNo } from '../utils/groupByRegNo';

function StudentList({ filter, year, searchTerm }) {
    const [students, setStudents] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedStudent, setSelectedStudent] = useState(null);
    const [schoolFilter, setSchoolFilter] = useState(''); // State for school filter

    // Fetch students based on the filter and search term
    useEffect(() => {
        const loadStudents = async() => {
            setLoading(true);
            setError(null);

            try {
                // Pass schoolFilter, year, searchTerm, and filter to fetchStudents API
                const data = await fetchStudents({ filter, year, searchTerm, schoolFilter });

                console.log("Fetched data:", data);

                if (Array.isArray(data)) {
                    const groupedData = groupByRegNo(data);

                    // If schoolFilter is set, we already filtered data based on school in the backend API call
                    setStudents(Object.values(groupedData)); // Set grouped data
                } else {
                    console.error('Fetched data is not an array:', data);
                    setStudents([]);
                    setError('Unexpected data format from server.');
                }
            } catch (error) {
                console.error('Error loading students:', error);
                setError('Failed to load student data.');
            } finally {
                setLoading(false);
            }
        };

        loadStudents();
    }, [filter, year, searchTerm, schoolFilter]); // Trigger when filters change

    // Handle student card click
    const handleStudentClick = (student) => {
        setSelectedStudent(student);
    };

    // Close student detail modal
    const closeModal = () => {
        setSelectedStudent(null);
    };

    // Render student cards
    const studentCards = students.flat().map(student => ( // Ensure flat array for rendering
        <
        div key = { student.Reg_No }
        onClick = {
            () => handleStudentClick(student) } >
        <
        StudentCard student = { student }
        /> <
        /div>
    ));

    // Change school filter
    const handleSchoolFilterChange = (school) => {
        setSchoolFilter(school); // Update the school filter to the selected school
    };

    // Loading and error handling
    if (loading) {
        return <p > Loading... < /p>;
    }

    if (error) {
        return <p className = "error-message" > { error } < /p>;
    }

    return ( <
        div className = "student-list" >
        <
        h2 style = {
            { marginLeft: '20px', fontSize: '30px' } } > Students < /h2>

        { /* Buttons for Scope, Sense, and SMEC */ } <
        div className = "button-container"
        style = {
            { marginLeft: '20px', marginBottom: '20px' } } >
        <
        button className = "student-filter-button"
        onClick = {
            () => handleSchoolFilterChange('SCOPE') } > SCOPE < /button> <
        button className = "student-filter-button"
        onClick = {
            () => handleSchoolFilterChange('SENSE') } > SENSE < /button> <
        button className = "student-filter-button"
        onClick = {
            () => handleSchoolFilterChange('SMEC') } > SMEC < /button> <
        button className = "student-filter-button"
        onClick = {
            () => handleSchoolFilterChange('') } > Clear Filter < /button> <
        /div>

        { /* Display student cards */ } <
        div className = "student-cards-container" > {
            students.length === 0 ? ( <
                p > No students found matching this criteria. < /p>
            ) : (
                studentCards
            )
        } <
        /div>

        { /* Modal for student detail */ } {
            selectedStudent && ( <
                StudentDetailModal student = { selectedStudent }
                onClose = { closeModal }
                />
            )
        } <
        /div>
    );
}

export default StudentList;
