import React, { useState, useEffect, useMemo } from 'react';
import StudentCard from './StudentCard';
import StudentDetailModal from './StudentDetailModal';
import './StudentList.css';
import { fetchStudents } from '../api/studentAPI';
import { groupByRegNo } from '../utils/groupByRegNo';

function StudentList({ filter, year, searchTerm, schoolFilter, typeFilter }) {
    const [students, setStudents] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedStudent, setSelectedStudent] = useState(null);

    // Normalize filters for case-insensitive comparison
    const normalizedSchoolFilter = schoolFilter ? schoolFilter.toLowerCase() : '';
    const normalizedTypeFilter = typeFilter ? typeFilter.toLowerCase() : '';
    const normalizedYearFilter = year ? year : '';

    useEffect(() => {
        const loadStudents = async() => {
            setLoading(true);
            setError(null);

            try {
                const data = await fetchStudents(filter, year, searchTerm, schoolFilter, typeFilter);
                console.log("Fetched data:", data);

                if (Array.isArray(data)) {
                    const groupedData = groupByRegNo(data);

                    const filteredStudents = Object.values(groupedData).filter(studentGroup => {
                        const firstStudent = studentGroup[0];

                        // Apply filter for year, school, and type
                        const matchesYear = normalizedYearFilter ?
                            firstStudent.Year === parseInt(normalizedYearFilter) : true;

                        const matchesSchool = normalizedSchoolFilter ?
                            firstStudent.School && firstStudent.School.toLowerCase() === normalizedSchoolFilter : true;

                        const matchesType = normalizedTypeFilter ?
                            firstStudent.Type && firstStudent.Type.toLowerCase() === normalizedTypeFilter : true;

                        return matchesYear && matchesSchool && matchesType;
                    });

                    setStudents(filteredStudents);
                } else {
                    console.error('Fetched data is not an array:', data);
                    setStudents([]);
                    setError('Unexpected data format from server.');
                }
            } catch (error) {
                console.error('Error loading students:', error);
                setError('Failed to load student data.');
            } finally {
                setLoading(false);
            }
        };

        loadStudents();
    }, [filter, year, searchTerm, normalizedSchoolFilter, normalizedTypeFilter]);

    const handleStudentClick = (student) => {
        setSelectedStudent(student);
    };

    const closeModal = () => {
        setSelectedStudent(null);
    };

    const studentCards = useMemo(() => {
        return students.map(student => ( <
            div key = { student.Reg_No }
            onClick = {
                () => handleStudentClick(student) } >
            <
            StudentCard student = { student }
            /> <
            /div>
        ));
    }, [students]);

    if (loading) {
        return <p > Loading... < /p>;
    }

    if (error) {
        return <p className = "error-message" > { error } < /p>;
    }

    return ( <
        div className = "student-list" >
        <
        h2 style = {
            { marginLeft: '20px', fontSize: '30px' } } > Students < /h2> <
        div className = "student-cards-container" > {
            students.length === 0 ? ( <
                p > No students found matching this criteria. < /p>
            ) : (
                studentCards
            )
        } <
        /div> {
            selectedStudent && ( <
                StudentDetailModal student = { selectedStudent }
                onClose = { closeModal }
                />
            )
        } <
        /div>
    );
}

export default StudentList;
