import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSquarePen } from '@fortawesome/free-solid-svg-icons';
import { faTrashCan } from '@fortawesome/free-solid-svg-icons';

import './JobNotifications.css';

const JobNotifications = () => {
    // State to manage job notifications
    const [notifications, setNotifications] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [showDetailModal, setShowDetailModal] = useState(false); // Modal for job details
    const [newNotification, setNewNotification] = useState({
        company: '',
        category: '',
        date: '',
        eligibleBranches: '',
        eligibleCriteria: '',
        ctc: '',
        website: '',
        jobLocation: '',
        description: ''
    });
    const [selectedNotification, setSelectedNotification] = useState(null); // To store the selected notification for details

    // Load notifications from localStorage on component mount
    useEffect(() => {
        const storedNotifications = JSON.parse(localStorage.getItem('jobNotifications')) || [];
        setNotifications(storedNotifications);
    }, []);

    // Save notifications to localStorage whenever they change
    useEffect(() => {
        localStorage.setItem('jobNotifications', JSON.stringify(notifications));
    }, [notifications]);

    // Handle changes in input fields
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewNotification((prev) => ({...prev, [name]: value }));
    };

    // Handle form submission
    const handleSubmit = (e) => {
        e.preventDefault();
        // Add new notification to the list
        setNotifications((prev) => [
            ...prev,
            {...newNotification, id: prev.length + 1 }
        ]);
        // Reset the notification form
        setNewNotification({
            company: '',
            category: '',
            date: '',
            eligibleBranches: '',
            eligibleCriteria: '',
            ctc: '',
            website: '',
            jobLocation: '',
            description: ''
        });
        // Close the modal
        setShowModal(false);
    };

    // Handle card click to view job details
    const handleCardClick = (notification) => {
        setSelectedNotification(notification);
        setShowDetailModal(true); // Open details modal
    };

    // Handle notification removal
    const handleRemoveNotification = (id) => {
        setNotifications((prev) => prev.filter(notification => notification.id !== id));
    };

    // Close the detail modal
    const closeDetailModal = () => {
        setShowDetailModal(false);
        setSelectedNotification(null);
    };

    return ( <
        div className = "job-notifications" >
        <
        h1 > Job Notifications < /h1> <
        button className = "upload-button_1"
        onClick = {
            () => setShowModal(true)
        } >
        <
        FontAwesomeIcon icon = { faSquarePen }
        /> < /
        button >

        <
        div className = "cards-container" > {
            notifications.map(notification => ( <
                div key = { notification.id }
                className = "job-card"
                onClick = {
                    () => handleCardClick(notification)
                } >
                <
                button className = "remove-buttonn"
                onClick = {
                    (e) => {
                        e.stopPropagation();
                        handleRemoveNotification(notification.id);
                    }
                } > < FontAwesomeIcon icon = "fa-solid fa-trash-can" / >
                <
                /button> <
                h3 > { notification.company } < /h3> <
                p > Date of Visit: { notification.date } < /p> <
                p > Category: { notification.category } < /p> < /
                div >
            ))
        } <
        /div>

        { /* Modal for job notification form */ } {
            showModal && ( <
                div className = "modal" >
                <
                div className = "modal-content" >
                <
                button className = "close-button"
                onClick = {
                    () => setShowModal(false)
                } > Close < /button> <
                h2 > Upload Job Notification < /h2> <
                form onSubmit = { handleSubmit } > { /* Form fields */ } <
                label >
                Company:
                <
                input type = "text"
                name = "company"
                value = { newNotification.company }
                onChange = { handleInputChange }
                required placeholder = "Enter company name" / >
                <
                /label> <
                label >
                Category:
                <
                input type = "text"
                name = "category"
                value = { newNotification.category }
                onChange = { handleInputChange }
                required placeholder = "Enter job category" / >
                <
                /label> <
                label >
                Date of Visit:
                <
                input type = "date"
                name = "date"
                value = { newNotification.date }
                onChange = { handleInputChange }
                required / >
                <
                /label> <
                label >
                Eligible Branches:
                <
                input type = "text"
                name = "eligibleBranches"
                value = { newNotification.eligibleBranches }
                onChange = { handleInputChange }
                required placeholder = "Enter eligible branches" / >
                <
                /label> <
                label >
                Eligible Criteria:
                <
                input type = "text"
                name = "eligibleCriteria"
                value = { newNotification.eligibleCriteria }
                onChange = { handleInputChange }
                required placeholder = "Enter eligible criteria" / >
                <
                /label> <
                label >
                CTC:
                <
                input type = "text"
                name = "ctc"
                value = { newNotification.ctc }
                onChange = { handleInputChange }
                required placeholder = "Enter CTC" / >
                <
                /label> <
                label >
                Website:
                <
                input type = "url"
                name = "website"
                value = { newNotification.website }
                onChange = { handleInputChange }
                required placeholder = "Enter website link" / >
                <
                /label> <
                label >
                Job Location:
                <
                input type = "text"
                name = "jobLocation"
                value = { newNotification.jobLocation }
                onChange = { handleInputChange }
                required placeholder = "Enter job location" / >
                <
                /label> <
                label >
                Description:
                <
                textarea name = "description"
                value = { newNotification.description }
                onChange = { handleInputChange }
                required placeholder = "Enter job description" / >
                <
                /label> <
                button type = "submit" > Submit < /button> < /
                form > <
                /div> < /
                div >
            )
        }

        { /* Modal for displaying job details */ } {
            showDetailModal && selectedNotification && ( <
                div className = "modal" >
                <
                div className = "modal-content" >
                <
                button className = "close-button"
                onClick = { closeDetailModal } > Close < /button> <
                h2 > { selectedNotification.company } < /h2> <
                p > < strong > Date of Visit: < /strong> {selectedNotification.date}</p >
                <
                p > < strong > Category: < /strong> {selectedNotification.category}</p >
                <
                p > < strong > Eligible Branches: < /strong> {selectedNotification.eligibleBranches}</p >
                <
                p > < strong > Eligible Criteria: < /strong> {selectedNotification.eligibleCriteria}</p >
                <
                p > < strong > CTC: < /strong> {selectedNotification.ctc}</p >
                <
                p > < strong > Website: < /strong> <a href={selectedNotification.website} target="_blank" rel="noopener noreferrer">{selectedNotification.website}</a > < /p> <
                p > < strong > Job Location: < /strong> {selectedNotification.jobLocation}</p >
                <
                p > < strong > Description: < /strong> {selectedNotification.description}</p >
                <
                /div> < /
                div >
            )
        } <
        /div>
    );
};

export default JobNotifications;
