import React from 'react';
import './StudentCard.css';

function StudentCard({ student }) {
    console.log('Student data in StudentCard:', student); // Log student object here

    if (!student) {
        return <div > Invalid student data < /div>;
    }

    // Extract applications
    const applications = student.Applications || [];

    // Determine the card color based on the application statuses
    const getStatusColor = (applications) => {
        // Check the application statuses to determine the card color
        if (applications.some(app => app.ApplicationStatus === 'Placed')) {
            return {
                background: '#9ae59a',
                border: 'green'
            }; // Light green for placed
        } else if (applications.some(app => app.ApplicationStatus === 'Shortlisted')) {
            return {
                background: '#ffc266',
                border: 'orange'
            }; // Light orange for shortlisted
        } else if (applications.some(app => app.ApplicationStatus === 'Not placed')) {
            return {
                background: '#ff8080',
                border: 'red'
            }; // Light red for not placed
        } else if (applications.some(app => app.ApplicationStatus === 'Applied')) {
            return {
                background: 'white',
                border: 'white'
            }; // Light grey for applied
        }
        return {
            background: '#ffffff',
            border: '#dee2e6'
        }; // Default to white
    };

    const { background, border } = getStatusColor(applications); // Get the color for the current status

    return ( <
        div className = "student-card"
        style = {
            { backgroundColor: background, borderColor: border }
        } >
        <
        h3 > { student.Name } < /h3> <
        p > Reg No: { student.Reg_No } < /p> <
        p > School: { student.School } < /p>  < /
        div >
    );
}

export default StudentCard;
