import React, { useState } from 'react';
import './VerticalNavBar.css';

function VerticalNavBar({ onFilterChange, onYearChange, toggleCompanyList, showCompanyList }) {
    const [tooltip, setTooltip] = useState('');
    const [tooltipPosition, setTooltipPosition] = useState({ top: 0, left: 0 });

    const handleMouseEnter = (text, event) => {
        setTooltip(text);
        const { top, left, height } = event.currentTarget.getBoundingClientRect();
        setTooltipPosition({ top: top + height + window.scrollY, left: left + window.scrollX });
    };

    const handleMouseLeave = () => {
        setTooltip('');
    };

    return ( <
        div className = "vertical-nav" >
        <
        button onClick = {
            () => onFilterChange('All Students')
        }
        onMouseEnter = {
            (e) => handleMouseEnter("View all students regardless of placement status.", e)
        }
        onMouseLeave = { handleMouseLeave } >
        All Students <
        /button>

        <
        button onClick = {
            () => onFilterChange('Placed')
        }
        onMouseEnter = {
            (e) => handleMouseEnter("View only the students who have been placed.", e)
        }
        onMouseLeave = { handleMouseLeave } >
        Placed Students <
        /button>

        <
        button onClick = {
            () => onFilterChange('Not placed')
        }
        onMouseEnter = {
            (e) => handleMouseEnter("View students who have not been placed.", e)
        }
        onMouseLeave = { handleMouseLeave } >
        Not Placed Students <
        /button>

        <
        select onChange = { e => onYearChange(e.target.value) }
        onMouseEnter = {
            (e) => handleMouseEnter("Select a year to filter students by their application year.", e)
        }
        onMouseLeave = { handleMouseLeave } >
        <
        option value = "" > Select Year < /option> <
        option value = "2022" > Year 2022 < /option> <
        option value = "2023" > Year 2023 < /option> <
        option value = "2024" > Year 2024 < /option> <
        option value = "2025" > Year 2025 < /option> <
        option value = "2026" > Year 2026 < /option> < /
        select >

        <
        button onClick = { toggleCompanyList }
        onMouseEnter = {
            (e) => handleMouseEnter(showCompanyList ? "Hide the list of companies visited." : "Show the list of companies visited.", e)
        }
        onMouseLeave = { handleMouseLeave } > { showCompanyList ? 'Hide Companies' : 'Companies Visited' } <
        /button>

        {
            tooltip && ( <
                div className = "tooltip"
                style = {
                    { top: tooltipPosition.top, left: tooltipPosition.left }
                } > { tooltip } <
                /div>
            )
        } <
        /div>
    );
}

export default VerticalNavBar;
