import React from 'react';
import './StudentCard.css';

function StudentCard({ student }) {
    console.log('Student data in StudentCard:', student);

    if (!student) {
        return <div > Invalid student data < /div>;
    }

    const companies = student.Companies || [];
    const statuses = student.Statuses || [];

    // Determine the card color based on the statuses
    const getStatusColor = (statuses) => {
        if (statuses.includes('Placed')) {
            return { background: '#d4edda', border: 'green' }; // Light green for placed
        } else if (statuses.includes('Shortlisted')) {
            return { background: '#fff3cd', border: 'orange' }; // Light orange for shortlisted
        } else if (statuses.includes('Not placed')) {
            return { background: '#f8d7da', border: 'red' }; // Light red for not placed
        } else if (statuses.includes('Applied')) {
            return { background: '#f8f9fa', border: 'white' }; // Light grey for applied
        }
        return { background: '#ffffff', border: '#dee2e6' }; // Default to white
    };

    const { background, border } = getStatusColor(statuses); // Get the color based on statuses

    return ( <
        div className = "student-card"
        style = {
            { backgroundColor: background, borderColor: border }
        } // Set dynamic background and border color
        >
        <
        h3 > { student.Name } < /h3> <
        p > Reg No: { student.Reg_No } < /p> <
        p > Year: { student.Year } < /p> <
        div className = "student-companies" >
        <
        ul > {
            companies.map((company, index) => ( <
                li key = { index }
                className = "company-status" >
                <
                strong className = "company-name" > { company } < /strong> <
                span className = "status-colon" >: < /span> <
                span className = "status" > { statuses[index] } < /span> < /
                li >
            ))
        } <
        /ul> < /
        div > <
        /div>
    );
}

export default StudentCard;
