import React, { useEffect, useState, useRef } from 'react';
import './Stats.css';

const Stats = () => {
        const [error, setError] = useState(null);
        const dataRef = useRef(null); // Ref to store the data persistently

        useEffect(() => {
            const fetchData = async() => {
                if (dataRef.current) return; // If data is already fetched, don't fetch again

                try {
                    const response = await fetch('http://localhost:1000/api/stats');
                    if (!response.ok) {
                        throw new Error('Network response was not ok');
                    }
                    const jsonData = await response.json();
                    dataRef.current = jsonData; // Store data in ref
                } catch (error) {
                    setError(error.message);
                }
            };

            fetchData();
        }, []); // Empty dependency array to fetch data once on mount

        const data = dataRef.current; // Access the fetched data

        return ( <
            div className = "stats-container" > {
                error && < p className = "error-message" > Error fetching data: { error } < /p>} {
                    data && data.length > 0 ? ( <
                        >
                        <
                        p className = "description" >
                        <
                        strong className = "red-text" > Description: < /strong>
                        It displays the count of total students, count of placed and not placed students in each year. <
                        /p> {
                            data.map((item, index) => ( <
                                div key = { index }
                                className = "stats-card" >
                                <
                                h4 className = "year" > { item.year } < /h4> <
                                p className = "total-students" >
                                Total Students: < span > { item.total } < /span> <
                                /p> <
                                p className = "placed" >
                                Placed: < span > { item.placed } < /span> <
                                /p> <
                                p className = "not-placed" >
                                Not Placed: < span > { item.notPlaced } < /span> <
                                /p> <
                                /div>
                            ))
                        } <
                        />
                    ) : ( <
                        p > No data available < /p>
                    )
                } <
                /div>
            );
        };

        export default Stats;
