import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './VerticalNavBar.css';
import { Link } from 'react-router-dom';

function VerticalNavBar({ onFilterChange, onYearChange, toggleCompanyList, showCompanyList }) {
    const [tooltip, setTooltip] = useState('');
    const [tooltipPosition, setTooltipPosition] = useState({ top: 0, left: 0 });
    const [schools, setSchools] = useState([]);
    const [types, setTypes] = useState([]);
    const [years, setYears] = useState([]);
    const [loading, setLoading] = useState(true);
    const [filters, setFilters] = useState({ school: '', type: '', year: '', status: 'all' });

    // Fetch data (schools, types, years) when component mounts
    useEffect(() => {
        const fetchData = async() => {
            try {
                const response = await axios.get('/api/filters');
                console.log('Full Response:', response.data);

                if (Array.isArray(response.data.schools)) setSchools(response.data.schools);
                if (Array.isArray(response.data.types)) setTypes(response.data.types);
                if (Array.isArray(response.data.years)) setYears(response.data.years);

                setLoading(false);
            } catch (error) {
                console.error('Error fetching data:', error);
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    const handleMouseEnter = (text, event) => {
        setTooltip(text);
        const { top, left, height } = event.currentTarget.getBoundingClientRect();
        const adjustedTop = top + height + window.scrollY;
        const adjustedLeft = left + window.scrollX;

        setTooltipPosition({ top: adjustedTop, left: adjustedLeft });
    };

    const handleMouseLeave = () => {
        setTooltip('');
    };

    // Update filter state and notify parent component
    const handleFilterChange = (key, value) => {
        setFilters(prevFilters => {
            const updatedFilters = {...prevFilters, [key]: value };
            onFilterChange(updatedFilters); // Pass updated filters to parent
            return updatedFilters;
        });
    };

    const handleYearChange = (event) => {
        const selectedYear = event.target.value;
        handleFilterChange('year', selectedYear);
        onYearChange(selectedYear); // Pass year separately to parent
    };

    const handleSchoolChange = (event) => {
        const selectedSchool = event.target.value;
        handleFilterChange('school', selectedSchool);
    };

    const handleTypeChange = (event) => {
        const selectedType = event.target.value;
        handleFilterChange('type', selectedType);
    };

    const handleStatusChange = (status) => {
        handleFilterChange('status', status);
    };

    if (loading) {
        return <div > Loading... < /div>;
    }

    return ( <
        div className = "vertical-nav" >
        <
        button onClick = {
            () => handleStatusChange('all') }
        onMouseEnter = {
            (e) => handleMouseEnter("View all students regardless of placement status.", e) }
        onMouseLeave = { handleMouseLeave } >
        All Students <
        /button>

        <
        button onClick = {
            () => handleStatusChange('placed') }
        onMouseEnter = {
            (e) => handleMouseEnter("View only the students who have been placed.", e) }
        onMouseLeave = { handleMouseLeave } >
        Placed Students <
        /button>

        <
        button onClick = {
            () => handleStatusChange('not placed') }
        onMouseEnter = {
            (e) => handleMouseEnter("View students who have not been placed.", e) }
        onMouseLeave = { handleMouseLeave } >
        Not Placed Students <
        /button>

        { /* School Dropdown */ } <
        select onChange = { handleSchoolChange }
        onMouseEnter = {
            (e) => handleMouseEnter("Select a school to filter students.", e) }
        onMouseLeave = { handleMouseLeave } >
        <
        option value = "" > Select School < /option> {
            schools.map((school, index) => ( <
                option key = { index }
                value = { school } > { school } < /option>
            ))
        } <
        /select>

        { /* Year Dropdown */ } <
        select onChange = { handleYearChange }
        onMouseEnter = {
            (e) => handleMouseEnter("Select a year to filter students by their application year.", e) }
        onMouseLeave = { handleMouseLeave } >
        <
        option value = "" > Select Year < /option> {
            years.map((year, index) => ( <
                option key = { index }
                value = { year } > Year { year } < /option>
            ))
        } <
        /select>

        { /* Type Dropdown */ } <
        select onChange = { handleTypeChange }
        onMouseEnter = {
            (e) => handleMouseEnter("Select a type to filter students.", e) }
        onMouseLeave = { handleMouseLeave } >
        <
        option value = "" > Select Type < /option> {
            types.map((type, index) => ( <
                option key = { index }
                value = { type } > { type } < /option>
            ))
        } <
        /select>

        <
        button onClick = { toggleCompanyList }
        onMouseEnter = {
            (e) => handleMouseEnter(showCompanyList ? "Hide the list of companies visited." : "Show the list of companies visited.", e) }
        onMouseLeave = { handleMouseLeave } > { showCompanyList ? 'Hide Companies' : 'Companies Visited' } <
        /button>

        { /* Job Notifications Link */ } <
        Link to = "/job-notifications"
        className = "nav-link"
        onMouseEnter = {
            (e) => handleMouseEnter("View job notifications.", e) }
        onMouseLeave = { handleMouseLeave } >
        Job Notifications <
        /Link>

        { /* Tooltip Rendering */ } {
            tooltip && ( <
                div className = "tooltip"
                style = {
                    { top: tooltipPosition.top, left: tooltipPosition.left } } > { tooltip } <
                /div>
            )
        } <
        /div>
    );
}

export default VerticalNavBar;
