import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './CompanyList.css'; // Ensure you import your CSS here

const CompanyList = ({ year }) => {
    const [companies, setCompanies] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const fetchCompanies = async() => {
        if (!year) return;

        setLoading(true);
        setError(null);

        try {
            const response = await axios.get(`http://localhost:1000/api/companies?year=${year}`);
            // Log the response to see if it's structured as expected
            console.log(response.data);
            setCompanies(response.data);
        } catch (error) {
            // Handle specific error cases for better debugging
            if (error.response) {
                // The request was made and the server responded with a status code
                console.error('Response data:', error.response.data);
                console.error('Response status:', error.response.status);
                setError(`Error: ${error.response.data.message || 'Failed to fetch companies.'}`);
            } else if (error.request) {
                // The request was made but no response was received
                console.error('Request data:', error.request);
                setError('No response received from the server.');
            } else {
                // Something happened in setting up the request
                console.error('Error message:', error.message);
                setError('Error setting up request.');
            }
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchCompanies();
    }, [year]);

    return ( <
            div className = "company-list" >
            <
            h2 > Companies Visited in { year } < /h2> {
            loading && < p > Loading... < /p>} {
            error && < p style = {
                { color: 'red' }
            } > { error } < /p>} {
            companies.length > 0 ? ( <
                ul > {
                    companies.map((company) => ( <
                        li key = { company.name } > { company.name } - Placed: { company.placedCount } <
                        /li>
                    ))
                } <
                /ul>
            ) : ( <
                p > No companies found
                for this year. < /p>
            )
        } <
        /div>
);
};

export default CompanyList;
