import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import './MoreData.css';

const MoreData = () => {
    const [years, setYears] = useState([]);
    const [firstYear, setFirstYear] = useState('');
    const [secondYear, setSecondYear] = useState('');
    const [data, setData] = useState(null);

    // Fetch unique years from the backend (assuming the backend provides all unique years)
    useEffect(() => {
        const fetchUniqueYears = async() => {
            try {
                const response = await axios.get('http://localhost:1000/api/suggest-years');
                setYears(response.data.years); // Assuming the API response has a `years` array
            } catch (error) {
                console.error('Error fetching years:', error);
            }
        };

        fetchUniqueYears();
    }, []);

    // Function to generate a range of next 5 years starting from the selected year
    const generateYearRange = (startYear) => {
        const start = parseInt(startYear, 10);
        return Array.from({ length: 5 }, (v, i) => start + i);
    };

    // Function to handle the compare logic, fetch data, and set the bar chart data
    const handleCompare = async() => {
        if (firstYear && secondYear) {
            const firstYearRange = generateYearRange(firstYear);
            const secondYearRange = generateYearRange(secondYear);

            try {
                const response = await axios.post('http://localhost:1000/api/compare-years', {
                    firstYearRange,
                    secondYearRange
                });

                const { firstYearData, secondYearData } = response.data;

                // Prepare data for the grouped bar chart
                const chartData = {
                    labels: response.data.companies, // Assuming the API returns company names
                    datasets: [{
                            label: `Data for ${firstYearRange.join(', ')}`,
                            data: firstYearData,
                            backgroundColor: 'rgba(75, 192, 192, 0.6)'
                        },
                        {
                            label: `Data for ${secondYearRange.join(', ')}`,
                            data: secondYearData,
                            backgroundColor: 'rgba(153, 102, 255, 0.6)'
                        }
                    ]
                };

                setData(chartData); // Update the chart with the fetched data
            } catch (error) {
                console.error('Error comparing year ranges:', error);
            }
        } else {
            console.log('Please select both years.');
        }
    };

    return ( <
        div className = "more-data-page" >
        <
        h2 > Compare Placement Statistics < /h2> <
        div className = "selectors" >
        <
        select value = { firstYear }
        onChange = {
            (e) => setFirstYear(e.target.value)
        } >
        <
        option value = "" > Select Starting Year
        for First Range < /option> {
        years.length > 0 ? years.map((year) => ( <
            option key = { year }
            value = { year } > { year } < /option>
        )) : < option value = "" > No Years Available < /option>} < /
        select >

        <
        select value = { secondYear }
        onChange = {
            (e) => setSecondYear(e.target.value)
        } >
        <
        option value = "" > Select Starting Year
        for Second Range < /option> {
        years.length > 0 ? years.map((year) => ( <
            option key = { year }
            value = { year } > { year } < /option>
        )) : < option value = "" > No Years Available < /option>} < /
        select >

        <
        button onClick = { handleCompare }
        className = "submit-btn" > Subkit < /button> < /
        div >

        { /* Render the bar chart if data is available */ } {
            data && ( <
                div className = "results" >
                <
                Bar data = { data }
                options = {
                    { responsive: true }
                }
                /> < /
                div >
            )
        } <
        /div>
    );
};

export default MoreData;
