import React, { useState } from 'react';
import './JobNotifications.css';

const JobNotifications = () => {
    // State to manage job notifications
    const [notifications, setNotifications] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [newNotification, setNewNotification] = useState({ company: '', position: '', date: '' });

    // Handle changes in input fields
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewNotification((prev) => ({...prev, [name]: value }));
    };

    // Handle form submission
    const handleSubmit = (e) => {
        e.preventDefault();
        // Add new notification to the list
        setNotifications((prev) => [
            ...prev,
            {...newNotification, id: prev.length + 1 }
        ]);
        // Reset the notification form
        setNewNotification({ company: '', position: '', date: '' });
        // Close the modal
        setShowModal(false);
    };

    return ( <
        div className = "job-notifications" >
        <
        h1 > Job Notifications < /h1> <
        button className = "upload-button_1"
        onClick = {
            () => setShowModal(true) } > ✏️{ /* Pencil icon for the upload feature */ } <
        /button> <
        table >
        <
        thead >
        <
        tr >
        <
        th > Company < /th> <
        th > Position < /th> <
        th > Date Posted < /th> <
        /tr> <
        /thead> <
        tbody > {
            notifications.map(notification => ( <
                tr key = { notification.id } >
                <
                td > { notification.company } < /td> <
                td > { notification.position } < /td> <
                td > { notification.date } < /td> <
                /tr>
            ))
        } <
        /tbody> <
        /table> { /* Modal for job notification form */ } {
            showModal && ( <
                div className = "modal" >
                <
                div className = "modal-content" >
                <
                button className = "close-button"
                onClick = {
                    () => setShowModal(false) } >
                Close <
                /button> <
                h2 > Upload Job Notification < /h2> <
                form onSubmit = { handleSubmit } >
                <
                label >
                Company:
                <
                input type = "text"
                name = "company"
                value = { newNotification.company }
                onChange = { handleInputChange }
                required placeholder = "Enter company name" /
                >
                <
                /label> <
                label >
                Position:
                <
                input type = "text"
                name = "position"
                value = { newNotification.position }
                onChange = { handleInputChange }
                required placeholder = "Enter job position" /
                >
                <
                /label> <
                label >
                Date Posted:
                <
                input type = "date"
                name = "date"
                value = { newNotification.date }
                onChange = { handleInputChange }
                required /
                >
                <
                /label> <
                button type = "submit" > Submit < /button> <
                /form> <
                /div> <
                /div>
            )
        } <
        /div>
    );
};

export default JobNotifications;
