import React, { useState, useEffect } from 'react';
import axios from 'axios';
import * as XLSX from 'xlsx'; // For .xlsx to .csv conversion
import './UploadPage.css';

const UploadPage = () => {
    const [uploadStatus, setUploadStatus] = useState('');
    const [statusType, setStatusType] = useState(''); // 'success' or 'error'
    const [selectedFile, setSelectedFile] = useState(null);
    const [recentUploads, setRecentUploads] = useState([]); // Initialize as an empty array

    // Handle file selection
    const handleFileChange = (e) => {
        setSelectedFile(e.target.files[0]);
        setUploadStatus('');
        setStatusType('');
    };

    // Handle file upload with format conversion
    const handleFileUpload = async() => {
        if (!selectedFile) {
            setStatusType('error');
            setUploadStatus('Please select a file to upload');
            return;
        }

        const fileExtension = selectedFile.name.split('.').pop().toLowerCase();

        let convertedFile;
        if (fileExtension === 'csv' || fileExtension === 'json') {
            convertedFile = selectedFile; // No conversion needed for CSV/JSON
        } else if (fileExtension === 'xlsx') {
            convertedFile = await convertExcelToCSV(selectedFile);
        } else {
            setStatusType('error');
            setUploadStatus('Unsupported file format. Please upload a .csv, .json, or .xlsx file.');
            return;
        }

        try {
            const formData = new FormData();
            formData.append('file', convertedFile);

            const response = await axios.post('/api/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });

            setStatusType('success');
            setUploadStatus(response.data.message || 'File uploaded successfully');
            setSelectedFile(null);
            fetchRecentUploads(); // Fetch recent uploads again after successful upload
        } catch (error) {
            console.error('Upload error:', error);
            setStatusType('error');
            setUploadStatus('Error uploading file');
        }
    };

    // Convert Excel (.xlsx) to CSV
    const convertExcelToCSV = (file) => {
        return new Promise((resolve, reject) => {
            const reader = new FileReader();
            reader.onload = (e) => {
                const data = e.target.result;
                const workbook = XLSX.read(data, { type: 'binary' });
                const sheetName = workbook.SheetNames[0]; // Assuming data is in the first sheet
                const worksheet = workbook.Sheets[sheetName];
                const csv = XLSX.utils.sheet_to_csv(worksheet);
                const blob = new Blob([csv], { type: 'text/csv' });
                const newFile = new File([blob], file.name.replace('.xlsx', '.csv'), { type: 'text/csv' });
                resolve(newFile);
            };
            reader.onerror = (error) => reject(error);
            reader.readAsBinaryString(file);
        });
    };

    // Fetch recent uploads
    const fetchRecentUploads = async() => {
        try {
            const response = await axios.get('/api/uploads'); // Adjust endpoint as needed
            setRecentUploads(response.data.uploads); // Assuming response contains 'uploads'
        } catch (error) {
            console.error('Error fetching recent uploads:', error);
        }
    };

    return ( <
        div className = "upload-container" >
        <
        h1 > File Upload < /h1> <
        input type = "file"
        onChange = { handleFileChange }
        accept = ".csv,.json,.xlsx" / >
        <
        button className = "upload-button"
        onClick = { handleFileUpload }
        disabled = {!selectedFile } >
        Upload <
        /button>

        {
            uploadStatus && ( <
                p className = { statusType === 'success' ? 'success-message' : 'error-message' } > { uploadStatus } <
                /p>
            )
        }

        { /* Recent Uploads Section */ } <
        h2 > Recent Uploads < /h2> <
        ul > {
            recentUploads.map((upload) => ( <
                li key = { upload.id } >
                <
                strong > { upload.filename } < /strong> - {new Date(upload.date).toLocaleDateString()} <
                /li>
            ))
        } <
        /ul> <
        /div>
    );
};

export default UploadPage;
