import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './VerticalNavBar.css';

function VerticalNavBar({
    onFilterChange,
    onYearChange,
    onSchoolChange,
    onTypeChange,
    schools,
    types,
    schoolFilter,
    yearFilter,
    typeFilter,
}) {
    const [tooltip, setTooltip] = useState('');
    const [tooltipPosition, setTooltipPosition] = useState({ top: 0, left: 0 });
    const [showCompanyList, setShowCompanyList] = useState(false);

    const toggleCompanyList = () => {
        setShowCompanyList(!showCompanyList);
    };

    return ( <
            div className = "vertical-nav" >
            <
            button onClick = {
                () => onFilterChange('All Students')
            } > All Students < /button> <
            button onClick = {
                () => onFilterChange('Placed')
            } > Placed Students < /button> <
            button onClick = {
                () => onFilterChange('Not placed')
            } > Not Placed Students < /button>

            <
            select value = { schoolFilter || '' }
            onChange = {
                (e) => onSchoolChange(e.target.value)
            } >
            <
            option value = "" > Select School < /option> {
            schools.length > 0 ? (
                schools.map((school) => ( <
                    option key = { school }
                    value = { school } > { school } < /option>
                ))
            ) : ( <
                option value = "" > Loading schools... < /option>
            )
        } <
        /select>

    <
    select value = { yearFilter || '' }
    onChange = {
            (e) => onYearChange(e.target.value)
        } >
        <
        option value = "" > Select Year < /option> { [2023, 2024, 2025].map((year) => ( <
            option key = { year }
            value = { year } > Year { year } < /option>
        ))
} <
/select>

<
select value = { typeFilter || '' }
onChange = {
        (e) => onTypeChange(e.target.value)
    } >
    <
    option value = "" > Select Type < /option> {
types.length > 0 ? (
    types.map((type) => ( <
        option key = { type }
        value = { type } > { type } < /option>
    ))
) : ( <
    option value = "" > Loading types... < /option>
)
} <
/select>

<
button onClick = { toggleCompanyList } > { showCompanyList ? 'Hide Companies' : 'Companies Visited' } <
    /button>

<
Link to = "/job-notifications"
className = "nav-link" > Job Notifications < /Link> < /
    div >
);
}

export default VerticalNavBar;
