import React from 'react';
import './StudentDetailModal.css'; // Make sure to style this modal

const StudentDetailModal = ({ student, onClose }) => {
    return ( <
            div className = "modal-overlay" >
            <
            div className = "modal-content" >
            <
            button className = "close-button"
            onClick = { onClose } > Close < /button> <
            h2 > Student Details < /h2> <
            p > < strong > Name: < /strong> {student.Name}</p >
            <
            p > < strong > Reg No: < /strong> {student.Reg_No}</p >
            <
            p > < strong > Year: < /strong> {student.Year}</p >

            { /* Displaying companies applied and their statuses */ } <
            h3 > Applications < /h3> {
            student.Applications && student.Applications.length > 0 ? ( <
                ul > {
                    student.ApplicationS.map((app, index) => ( <
                        li key = { index } >
                        <
                        strong > Company: < /strong> {app.Company} - <strong>Status:</strong > { app.Status } <
                        /li>
                    ))
                } <
                /ul>
            ) : ( <
                p > No applications found
                for this student. < /p>
            )
        } <
        /div> < /
        div >
);
};

export default StudentDetailModal;
