// MoreData.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2'; // Ensure you use this somewhere in your component
import './MoreData.css'; // Ensure you have your CSS styles

const MoreData = () => {
    const [halfDecades, setHalfDecades] = useState([]);
    const [firstHalfDecade, setFirstHalfDecade] = useState('');
    const [secondHalfDecade, setSecondHalfDecade] = useState('');
    const [data, setData] = useState(null);

    useEffect(() => {
        const fetchUniqueYears = async() => {
            try {
                const response = await axios.get('http://localhost:1000/api/suggest-half-decades'); // Update with your backend URL

                console.log('Response data:', response.data); // Log the response data

                // Extract the halfDecades array from the response object
                const { halfDecades } = response.data;

                // Log the extracted halfDecades
                console.log('Extracted halfDecades:', halfDecades);

                // Check if halfDecades is an array
                if (!Array.isArray(halfDecades)) {
                    console.error('halfDecades is not an array:', halfDecades);
                    throw new Error('halfDecades is not an array');
                }

                setHalfDecades(halfDecades); // Set the state with the correct array
            } catch (error) {
                console.error('Error fetching unique years:', error);
            }
        };

        fetchUniqueYears();
    }, []);

    // Handle comparison logic (placeholder implementation)
    const handleCompare = () => {
        // You would implement your comparison logic here based on selected decades.
        console.log(`Comparing ${firstHalfDecade} and ${secondHalfDecade}`);

        // Sample data to simulate fetching or calculating comparison data
        const sampleData = {
            labels: ['Placed', 'Category 2'],
            datasets: [{
                    label: `Data for ${firstHalfDecade}`,
                    data: [10, 20], // Sample values
                    backgroundColor: 'rgba(75,192,192,0.4)',
                },
                {
                    label: `Data for ${secondHalfDecade}`,
                    data: [15, 25], // Sample values
                    backgroundColor: 'rgba(153,102,255,0.4)',
                },
            ],
        };

        setData(sampleData); // Set the comparison data
    };

    return ( <
        div className = "more-data-page" >
        <
        h2 > Compare Placement Statistics < /h2> <
        div className = "selectors" >
        <
        select value = { firstHalfDecade }
        onChange = {
            (e) => setFirstHalfDecade(e.target.value)
        } >
        <
        option value = "" > Select First Half - Decade < /option> {
        halfDecades.length > 0 ? halfDecades.map((hd) => ( <
            option key = { hd }
            value = { hd } > { hd } < /option>
        )) : < option value = "" > No Years Available < /option>} < /
        select > <
        select value = { secondHalfDecade }
        onChange = {
            (e) => setSecondHalfDecade(e.target.value)
        } >
        <
        option value = "" > Select Second Half - Decade < /option> {
        halfDecades.length > 0 ? halfDecades.map((hd) => ( <
            option key = { hd }
            value = { hd } > { hd } < /option>
        )) : < option value = "" > No Years Available < /option>} < /
        select > <
        button onClick = { handleCompare } > Compare < /button> < /
        div >

        { /* Render the chart only if data is present */ } {
            data && ( <
                div className = "results" >
                <
                Bar data = { data }
                options = {
                    { responsive: true }
                }
                /> < /
                div >
            )
        } <
        /div>
    );
};

export default MoreData;
