// Monitoring.js
import React, { useState, useEffect } from 'react';
import axios from 'axios'; // To make API requests
import { Bar } from 'react-chartjs-2'; // For the chart
import 'chart.js/auto';

function Monitoring() {
    const [numYears, setNumYears] = useState(1);
    const [selectedYears, setSelectedYears] = useState([]);
    const [companyList, setCompanyList] = useState([]);
    const [selectedCompany, setSelectedCompany] = useState('');
    const [placementData, setPlacementData] = useState(null);

    useEffect(() => {
        // Fetch the list of companies from your API
        axios.get('/api/companies').then((response) => {
            setCompanyList(response.data);
        });
    }, []);

    const handleNumYearsChange = (e) => {
        setNumYears(Number(e.target.value));
        setSelectedYears(new Array(Number(e.target.value)).fill('')); // Reset year selection
    };

    const handleYearChange = (index, e) => {
        const newSelectedYears = [...selectedYears];
        newSelectedYears[index] = e.target.value;
        setSelectedYears(newSelectedYears);
    };

    const handleCompanyChange = (e) => {
        setSelectedCompany(e.target.value);
    };

    const fetchPlacementData = () => {
        const payload = {
            years: selectedYears,
            company: selectedCompany || null, // If no company is selected, send null
        };

        axios.post('/api/placements', payload).then((response) => {
            setPlacementData(response.data);
        });
    };

    const chartData = {
        labels: selectedYears,
        datasets: [{
            label: `Placements for ${selectedCompany || 'All Companies'}`,
            data: placementData ? placementData.map(item => item.placements) : [],
            backgroundColor: 'rgba(75, 192, 192, 0.6)',
            borderColor: 'rgba(75, 192, 192, 1)',
            borderWidth: 1,
        }],
    };

    return ( <
        div >
        <
        h2 > Monitoring Placements < /h2> <
        label htmlFor = "numYears" > Select number of years to compare: < /label> <
        input id = "numYears"
        type = "number"
        value = { numYears }
        onChange = { handleNumYearsChange }
        min = "1"
        max = "5" /
        >

        {
            Array.from({ length: numYears }).map((_, index) => ( <
                div key = { index } >
                <
                label htmlFor = { `year${index}` } > Select Year { index + 1 }: < /label> <
                select id = { `year${index}` }
                value = { selectedYears[index] || '' }
                onChange = {
                    (e) => handleYearChange(index, e) } >
                <
                option value = "" > Select Year < /option> <
                option value = "2023" > 2023 < /option> <
                option value = "2022" > 2022 < /option> <
                option value = "2021" > 2021 < /option> { /* Add more options as necessary */ } <
                /select> <
                /div>
            ))
        }

        <
        label htmlFor = "company" > Select Company(Optional): < /label> <
        select id = "company"
        value = { selectedCompany }
        onChange = { handleCompanyChange } >
        <
        option value = "" > All Companies < /option> {
            companyList.map((company) => ( <
                option key = { company.id }
                value = { company.name } > { company.name } <
                /option>
            ))
        } <
        /select>

        <
        button onClick = { fetchPlacementData } > Get Placement Data < /button>

        {
            placementData && ( <
                div style = {
                    { width: '600px', height: '400px' } } >
                <
                Bar data = { chartData }
                /> <
                /div>
            )
        } <
        /div>
    );
}

export default Monitoring;
