import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './Navbar.css';
import logo from './logo.png';
import { FaSun, FaMoon } from 'react-icons/fa'; // Import sun and moon icons

function Navbar({ onSearch }) {
    const [searchTerm, setSearchTerm] = useState('');
    const [isDarkMode, setIsDarkMode] = useState(false); // State to track mode
    const navigate = useNavigate();
    const location = useLocation();
    const isLoggedIn = !!localStorage.getItem('authToken');

    const handleSearchChange = (e) => {
        const value = e.target.value;
        setSearchTerm(value);
        if (onSearch) {
            onSearch(value);
        }
    };

    const handleHomeClick = (e) => {
        e.preventDefault();
        navigate('/');
    };

    const handleAboutClick = (e) => {
        e.preventDefault();
        navigate('/about');
    };

    const handleContactClick = (e) => {
        e.preventDefault();
        navigate('/contact');
    };

    const handleLogout = () => {
        localStorage.removeItem('authToken');
        navigate('/login');
    };

    const toggleMode = () => {
        setIsDarkMode((prevMode) => !prevMode); // Toggle the mode
        document.body.classList.toggle('dark-mode', !isDarkMode); // Toggle the dark mode class
    };

    return ( <
        nav className = { `navbar ${isDarkMode ? 'dark' : ''}` } >
        <
        img src = { logo }
        alt = "Logo"
        className = "navbar-logo" / >
        <
        ul className = "navbar-menu" > {
            isLoggedIn && ( <
                >
                <
                li >
                <
                a href = "/"
                onClick = { handleHomeClick }
                className = { location.pathname === '/' ? 'active' : '' } >
                Home <
                /a> <
                /li> <
                li >
                <
                a href = "/about"
                onClick = { handleAboutClick }
                className = { location.pathname === '/about' ? 'active' : '' } >
                About <
                /a> <
                /li> <
                li >
                <
                a href = "/contact"
                onClick = { handleContactClick }
                className = { location.pathname === '/contact' ? 'active' : '' } >
                Contact <
                /a> <
                /li> <
                />
            )
        } <
        /ul> <
        div className = "navbar-search" >
        <
        input type = "text"
        placeholder = "Search by Registration Number"
        value = { searchTerm }
        onChange = { handleSearchChange }
        /> <
        /div> <
        div className = "mode-toggle-icon"
        onClick = { toggleMode } > { isDarkMode ? < FaSun / > : < FaMoon / > } { /* Toggle between sun and moon icons */ } <
        /div> {
            isLoggedIn && ( <
                button className = "logout-button"
                onClick = { handleLogout } >
                Logout <
                /button>
            )
        } <
        /nav>
    );
}

export default Navbar;
