import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import 'chart.js/auto';
import './Monitoring.css';

function Monitoring() {
    const [numYears, setNumYears] = useState(2);
    const [selectedYears, setSelectedYears] = useState([]);
    const [companyList, setCompanyList] = useState([]);
    const [selectedCompany, setSelectedCompany] = useState('');
    const [placementData, setPlacementData] = useState([]);

    // Fetch company list from the database based on the selected year
    const fetchCompanies = async(year) => {
        try {
            const response = await axios.get('/api/companies', {
                params: { year } // Pass the selected year as a query parameter
            });
            if (Array.isArray(response.data)) {
                setCompanyList(response.data);
            } else {
                console.error('Invalid company data structure:', response.data);
            }
        } catch (error) {
            console.error('Error fetching companies:', error);
        }
    };

    // Fetch companies whenever selectedYears changes
    useEffect(() => {
        if (selectedYears.length > 0) {
            fetchCompanies(selectedYears[0]); // Fetch companies based on the first selected year
        }
    }, [selectedYears]);

    const handleNumYearsChange = (e) => {
        const num = Number(e.target.value);
        if (num >= 2 && num <= 20) {
            setNumYears(num);
            setSelectedYears(new Array(num).fill(''));
        } else {
            alert("Please select a number of years between 2 and 20.");
        }
    };

    // Ensure same years can't be selected multiple times
    const handleYearChange = (index, e) => {
        const newYear = e.target.value;
        if (selectedYears.includes(newYear)) {
            alert('Year already selected! Please select a different year.');
            return;
        }
        const newSelectedYears = [...selectedYears];
        newSelectedYears[index] = newYear;
        setSelectedYears(newSelectedYears);
    };

    const handleCompanyChange = (e) => {
        setSelectedCompany(e.target.value);
    };

    // Fetch the placement data based on selected years and company
    const fetchPlacementData = async() => {
        const payload = {
            years: selectedYears,
            company: selectedCompany || null,
        };

        try {
            const response = await axios.post('/api/placement', payload);
            if (Array.isArray(response.data)) {
                setPlacementData(response.data);
            } else {
                console.error('Invalid placement data structure:', response.data);
            }
        } catch (error) {
            console.error('Error fetching placement data:', error);
        }
    };

    const chartData = {
        labels: [...new Set(placementData.map(item => item.ApplicationStatus))],
        datasets: selectedYears.map((year, index) => {
            const yearData = placementData.filter(item => item.Year === year);
            const counts = {};
            yearData.forEach(item => {
                counts[item.ApplicationStatus] = (counts[item.ApplicationStatus] || 0) + item.count; // Ensure to use count from the response
            });

            return {
                label: `${year} Placements`,
                data: labels.map(label => counts[label] || 0), // Data needs to reflect counts for each label
                backgroundColor: `rgba(${(index + 1) * 50}, ${(index + 1) * 100}, ${(index + 1) * 150}, 0.6)`,
                borderColor: `rgba(${(index + 1) * 50}, ${(index + 1) * 100}, ${(index + 1) * 150}, 1)`,
                borderWidth: 1,
            };
        }),
    };


    return ( <
            div className = "monitoring-container" >
            <
            h2 > Monitoring Placements < /h2> <
            label htmlFor = "numYears" > Select number of years to compare(min 2, max 20): < /label> <
            input id = "numYears"
            type = "number"
            value = { numYears }
            onChange = { handleNumYearsChange }
            min = "2"
            max = "20" /
            >
            <
            div className = "year-selection-container" > {
                Array.from({ length: numYears }).map((_, index) => ( <
                        div key = { index }
                        className = "year-dropdown" >
                        <
                        label htmlFor = { `year${index}` } > Year { index + 1 }: < /label> <
                        select id = { `year${index}` }
                        value = { selectedYears[index] || '' }
                        onChange = {
                            (e) => handleYearChange(index, e)
                        } >
                        <
                        option value = "" > Select Year < /option> {
                        Array.from({ length: 20 }, (_, i) => 2025 - i).map((year) => ( <
                            option key = { year }
                            value = { year } > { year } < /option>
                        ))
                    } <
                    /select> < /
                    div >
                ))
        } <
        /div> <
    label htmlFor = "company" > Select Company(Optional): < /label> <
    select id = "company"
    value = { selectedCompany }
    onChange = { handleCompanyChange } >
        <
        option value = "" > All Companies < /option> {
    companyList.length > 0 ? (
        companyList.map((company) => ( <
            option key = { company._id }
            value = { company.name } > { company.name } < /option>
        ))
    ) : ( <
        option value = "" > No Companies Available < /option>
    )
} <
/select> <
button className = "onee"
onClick = { fetchPlacementData } > Submit < /button> {
placementData.length > 0 && ( <
div className = "monitoring-chart" >
<
Bar data = { chartData }
/> < /
div >
)
} <
/div>
);
}

export default Monitoring;
