import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './VerticalNavBar.css';
import { Link } from 'react-router-dom';

function VerticalNavBar({ onFilterChange, onYearChange, toggleCompanyList, showCompanyList }) {
    const [tooltip, setTooltip] = useState('');
    const [tooltipPosition, setTooltipPosition] = useState({ top: 0, left: 0 });
    const [schools, setSchools] = useState([]);
    const [types, setTypes] = useState([]);
    const [years, setYears] = useState([]);
    const [students, setStudents] = useState([]);

    useEffect(() => {
        const fetchData = async() => {
            try {
                const [responseSchools, responseTypes, responseYears] = await Promise.all([
                    axios.get('/api/schools'),
                    axios.get('/api/types'),
                    axios.get('/api/years')
                ]);

                setSchools(responseSchools.data);
                setTypes(responseTypes.data);
                setYears(responseYears.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    useEffect(() => {
        const fetchFilteredStudents = async() => {
            try {
                // Fetch students with the filters
                const { filter, year, school, type } = onFilterChange;
                const queryParams = new URLSearchParams();

                if (filter) queryParams.append('filter', filter);
                if (year) queryParams.append('year', year);
                if (school) queryParams.append('school', school);
                if (type) queryParams.append('type', type);

                const response = await axios.get(`/api/students?${queryParams.toString()}`);
                setStudents(response.data); // Update students data based on filter
            } catch (error) {
                console.error('Error fetching filtered students:', error);
            }
        };

        fetchFilteredStudents();
    }, [onFilterChange]);

    const handleMouseEnter = (text, event) => {
        setTooltip(text);
        const { top, left, height } = event.currentTarget.getBoundingClientRect();
        setTooltipPosition({ top: top + height + window.scrollY, left: left + window.scrollX });
    };

    const handleMouseLeave = () => {
        setTooltip('');
    };

    const handleSchoolChange = (event) => {
        const selectedSchool = event.target.value;
        console.log("Selected School:", selectedSchool);
        onFilterChange('School', selectedSchool); // Pass to the parent component
    };

    const handleTypeChange = (event) => {
        const selectedType = event.target.value;
        onFilterChange('Type', selectedType); // Pass to the parent component
    };

    return ( <
            div className = "vertical-nav" >
            <
            button onClick = {
                () => onFilterChange('Status', 'All Students')
            }
            onMouseEnter = {
                (e) => handleMouseEnter("View all students regardless of placement status.", e)
            }
            onMouseLeave = { handleMouseLeave } >
            All Students <
            /button>

            <
            button onClick = {
                () => onFilterChange('Status', 'Placed')
            }
            onMouseEnter = {
                (e) => handleMouseEnter("View only the students who have been placed.", e)
            }
            onMouseLeave = { handleMouseLeave } >
            Placed Students <
            /button>

            <
            button onClick = {
                () => onFilterChange('Status', 'Not placed')
            }
            onMouseEnter = {
                (e) => handleMouseEnter("View students who have not been placed.", e)
            }
            onMouseLeave = { handleMouseLeave } >
            Not Placed Students <
            /button>

            <
            select onChange = { handleSchoolChange }
            onMouseEnter = {
                (e) => handleMouseEnter("Select a school to filter students.", e)
            }
            onMouseLeave = { handleMouseLeave } >
            <
            option value = "" > Select School < /option> {
            schools.map((school) => ( <
                option key = { school }
                value = { school } > { school } < /option>
            ))
        } <
        /select>

    <
    select onChange = {
        (e) => onYearChange(e.target.value)
    }
    onMouseEnter = {
        (e) => handleMouseEnter("Select a year to filter students by their application year.", e)
    }
    onMouseLeave = { handleMouseLeave } >
        <
        option value = "" > Select Year < /option> {
    years.map((year) => ( <
        option key = { year }
        value = { year } > Year { year } < /option>
    ))
} <
/select>

<
select onChange = { handleTypeChange }
onMouseEnter = {
    (e) => handleMouseEnter("Select a type to filter students.", e)
}
onMouseLeave = { handleMouseLeave } >
    <
    option value = "" > Select Type < /option> {
types.map((type) => ( <
    option key = { type }
    value = { type } > { type } < /option>
))
} <
/select>

<
button onClick = { toggleCompanyList }
onMouseEnter = {
    (e) => handleMouseEnter(showCompanyList ? "Hide the list of companies visited." : "Show the list of companies visited.", e)
}
onMouseLeave = { handleMouseLeave } > { showCompanyList ? 'Hide Companies' : 'Companies Visited' } <
    /button>

<
Link to = "/job-notifications"
className = "nav-link"
onMouseEnter = {
    (e) => handleMouseEnter("View job notifications.", e)
}
onMouseLeave = { handleMouseLeave } >
    Job Notifications <
    /Link>

{
    tooltip && ( <
        div className = "tooltip"
        style = {
            { top: tooltipPosition.top, left: tooltipPosition.left }
        } > { tooltip } <
        /div>
    )
} <
/div>
);
}

export default VerticalNavBar;
