// src/components/CTCChart.js
import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import axios from 'axios';

const CTCChart = () => {
    const [chartData, setChartData] = useState(null);

    useEffect(() => {
        // Fetch the data from the backend
        axios.get('/api/students/ctc-by-school-year')
            .then(response => {
                const data = response.data;

                // Prepare data for the chart
                const labels = [...new Set(data.map(item => `${item.school} (${item.year})`))];
                const avgCTC = data.map(item => item.avgCTC);

                setChartData({
                    labels,
                    datasets: [{
                        label: 'Average CTC (LPA)',
                        data: avgCTC,
                        backgroundColor: 'rgba(75, 192, 192, 0.6)',
                        borderColor: 'rgba(75, 192, 192, 1)',
                        borderWidth: 1
                    }]
                });
            })
            .catch(error => console.error("Error fetching data:", error));
    }, []);

    return ( <
        div >
        <
        h2 > Average CTC by School and Year < /h2> {
            chartData ? ( <
                Bar data = { chartData }
                options = {
                    {
                        scales: {
                            y: {
                                beginAtZero: true,
                                title: {
                                    display: true,
                                    text: 'CTC in LPA'
                                }
                            }
                        }
                    }
                }
                />
            ) : ( <
                p > Loading chart data... < /p>
            )
        } <
        /div>
    );
};

export default CTCChart;
