import React, { useState, useEffect } from 'react';
import axios from 'axios'; // To make API requests
import { Bar } from 'react-chartjs-2'; // For the chart
import 'chart.js/auto';
import './Monitoring.css'; // Import the CSS file

function Monitoring() {
    const [numYears, setNumYears] = useState(2); // Default number of years
    const [selectedYears, setSelectedYears] = useState([]); // Selected years
    const [companyList, setCompanyList] = useState([]); // Company list from API
    const [selectedCompany, setSelectedCompany] = useState(''); // Selected company
    const [placementData, setPlacementData] = useState([]); // Placement data for chart

    // Fetch the list of companies on component mount
    useEffect(() => {
        const fetchCompanies = async() => {
            try {
                const response = await axios.get('/api/company'); // Ensure this matches your backend route
                console.log('Company Response:', response.data); // Debugging line

                if (Array.isArray(response.data)) {
                    setCompanyList(response.data);
                } else {
                    console.error('Invalid company data structure:', response.data);
                }
            } catch (error) {
                console.error('Error fetching companies:', error);
            }
        };

        fetchCompanies();
    }, []);

    // Handle changes in the number of years to compare
    const handleNumYearsChange = (e) => {
        const num = Number(e.target.value);
        if (num >= 2 && num <= 20) { // Allow up to 20 years
            setNumYears(num);
            setSelectedYears(new Array(num).fill('')); // Reset the selected years array
        } else {
            alert("Please select a number of years between 2 and 20.");
        }
    };

    // Handle year selection change
    const handleYearChange = (index, e) => {
        const newSelectedYears = [...selectedYears];
        newSelectedYears[index] = e.target.value;
        setSelectedYears(newSelectedYears);
    };

    // Handle company selection change
    const handleCompanyChange = (e) => {
        setSelectedCompany(e.target.value);
    };

    // Fetch placement data based on selected years and company
    const fetchPlacementData = async() => {
        const payload = {
            years: selectedYears,
            company: selectedCompany || null,
        };

        try {
            const response = await axios.post('/api/placement', payload);
            if (Array.isArray(response.data)) {
                // Transform data to match the expected format
                const transformedData = response.data.reduce((acc, item) => {
                    const yearIndex = selectedYears.indexOf(item.year); // Assuming `item.year` exists
                    if (yearIndex >= 0) {
                        acc[yearIndex] = acc[yearIndex] || { status: item.ApplicationStatus, count: 0 };
                        acc[yearIndex].count += item.count || 0; // Increment count
                    }
                    return acc;
                }, []);
                setPlacementData(transformedData);
            } else {
                console.error('Invalid placement data structure:', response.data);
            }
        } catch (error) {
            console.error('Error fetching placement data:', error);
        }
    };

    // Prepare data for the chart
    const chartData = {
        labels: placementData.map(item => item.status), // Using ApplicationStatus for labels
        datasets: selectedYears.map((year, index) => ({
            label: `${year} Placements`,
            data: placementData[index] ? placementData[index].count : [], // Use count based on index
            backgroundColor: `rgba(${(index + 1) * 50}, ${(index + 1) * 100}, ${(index + 1) * 150}, 0.6)`,
            borderColor: `rgba(${(index + 1) * 50}, ${(index + 1) * 100}, ${(index + 1) * 150}, 1)`,
            borderWidth: 1,
        })),
    };

    return ( <
        div className = "monitoring-container" >
        <
        h2 > Monitoring Placements < /h2>

        <
        label htmlFor = "numYears" > Select number of years to compare(min 2, max 20): < /label> <
        input id = "numYears"
        type = "number"
        value = { numYears }
        onChange = { handleNumYearsChange }
        min = "2"
        max = "20" // Set max to 20
        /
        >

        { /* Year selection dropdowns side by side */ } <
        div className = "year-selection-container" > {
            Array.from({ length: numYears }).map((_, index) => ( <
                div key = { index }
                className = "year-dropdown" >
                <
                label htmlFor = { `year${index}` } > Year { index + 1 }: < /label> <
                select id = { `year${index}` }
                value = { selectedYears[index] || '' }
                onChange = {
                    (e) => handleYearChange(index, e) } >
                <
                option value = "" > Select Year < /option> {
                    Array.from({ length: 20 }, (_, i) => 2025 - i).map(year => ( <
                        option key = { year }
                        value = { year } > { year } < /option>
                    ))
                } <
                /select> <
                /div>
            ))
        } <
        /div>

        { /* Company selection dropdown */ } <
        label htmlFor = "company" > Select Company(Optional): < /label> <
        select id = "company"
        value = { selectedCompany }
        onChange = { handleCompanyChange } >
        <
        option value = "" > All Companies < /option> {
            companyList.length > 0 ? (
                companyList.map((company) => ( <
                    option key = { company._id }
                    value = { company.name } > { company.name || 'Invalid Company' } < /option>
                ))
            ) : ( <
                option value = "" > No Companies Available < /option>
            )
        } <
        /select>

        <
        button className = "onee"
        onClick = { fetchPlacementData } > Submit < /button>

        { /* Display the chart if there is placement data */ } {
            placementData.length > 0 && ( <
                div className = "monitoring-chart" >
                <
                Bar data = { chartData }
                /> <
                /div>
            )
        } <
        /div>
    );
}

export default Monitoring;
