import axios from 'axios';

// api/studentAPI.js
export const fetchStudents = async(filter, year, searchTerm, schoolFilter, typeFilter) => {
    try {
        // Build query parameters dynamically
        let url = '/api/students';
        const params = new URLSearchParams();

        // Add filters and parameters to the query string
        if (filter) params.append('filter', filter);
        if (year) params.append('year', year);
        if (searchTerm) params.append('searchTerm', searchTerm);
        if (schoolFilter) params.append('schoolFilter', schoolFilter);
        if (typeFilter) params.append('typeFilter', typeFilter);

        // Append query parameters to the base URL
        url += `?${params.toString()}`;

        // Send the GET request using axios
        const response = await axios.get(url);

        // Return the data
        return response.data;
    } catch (error) {
        console.error('Error fetching students:', error);
        throw error;
    }
};
