import React, { useState, useEffect } from 'react';
import axios from 'axios'; // To make API requests
import { Bar } from 'react-chartjs-2'; // For the chart
import 'chart.js/auto';
import './Monitoring.css'; // Import the CSS file

function Monitoring() {
    const [numYears, setNumYears] = useState(2); // Set default to 2
    const [selectedYears, setSelectedYears] = useState([]);
    const [companyList, setCompanyList] = useState([]);
    const [selectedCompany, setSelectedCompany] = useState('');
    const [placementData, setPlacementData] = useState([]);

    useEffect(() => {
        // Fetch the list of companies from your API
        axios.get('http://localhost:1000/api/companies') // Ensure this matches your backend route
            .then((response) => {
                // Add "All Companies" option to the fetched companies
                setCompanyList([{ name: 'All Companies' }, ...response.data]);
            })
            .catch((error) => {
                console.error('Error fetching companies:', error);
            });
    }, []);

    const handleNumYearsChange = (e) => {
        const num = Number(e.target.value);
        if (num >= 2) { // Ensure minimum of 2 years
            setNumYears(num);
            setSelectedYears(new Array(num).fill(''));
        } else {
            alert("Please select at least 2 years.");
        }
    };

    const handleYearChange = (index, e) => {
        const newSelectedYears = [...selectedYears];
        newSelectedYears[index] = e.target.value;
        setSelectedYears(newSelectedYears);
    };

    const handleCompanyChange = (e) => {
        setSelectedCompany(e.target.value);
    };

    const fetchPlacementData = () => {
        const payload = {
            years: selectedYears,
            company: selectedCompany !== 'All Companies' ? selectedCompany : null, // Send null for "All Companies"
        };

        axios.post('http://localhost:1000/api/placements', payload) // Ensure this endpoint is defined in your backend
            .then((response) => {
                setPlacementData(response.data);
            })
            .catch((error) => {
                console.error('Error fetching placement data:', error);
            });
    };

    const chartData = {
        labels: placementData.map(item => item.status),
        datasets: [{
            label: `Placements for ${selectedCompany || 'All Companies'}`,
            data: placementData.map(item => item.count),
            backgroundColor: 'rgba(75, 192, 192, 0.6)',
            borderColor: 'rgba(75, 192, 192, 1)',
            borderWidth: 1,
        }],
    };

    return ( <
        div className = "monitoring-container" >
        <
        h2 > Monitoring Placements < /h2> <
        label htmlFor = "numYears" > Select number of years to compare(min 2): < /label> <
        input id = "numYears"
        type = "number"
        value = { numYears }
        onChange = { handleNumYearsChange }
        min = "2" // Set minimum to 2
        max = "5" /
        >

        {
            Array.from({ length: numYears }).map((_, index) => ( <
                div key = { index } >
                <
                label htmlFor = { `year${index}` } > Select Year { index + 1 }: < /label> <
                select id = { `year${index}` }
                value = { selectedYears[index] || '' }
                onChange = {
                    (e) => handleYearChange(index, e) } >
                <
                option value = "" > Select Year < /option> {
                    [2025, 2024, 2023, 2022, 2021].map(year => ( <
                        option key = { year }
                        value = { year } > { year } < /option>
                    ))
                } <
                /select> <
                /div>
            ))
        }

        <
        label htmlFor = "company" > Select Company(Optional): < /label> <
        select id = "company"
        value = { selectedCompany }
        onChange = { handleCompanyChange } >
        <
        option value = "" > All Companies < /option> {/ * Default option * /} {
            companyList.map((company, index) => ( <
                option key = { index }
                value = { company.name } > { company.name } < /option>
            ))
        } <
        /select>

        <
        button onClick = { fetchPlacementData } > Get Placement Data < /button>

        {
            placementData.length > 0 && ( <
                div className = "monitoring-chart" >
                <
                Bar data = { chartData }
                /> <
                /div>
            )
        } <
        /div>
    );
}

export default Monitoring;
