import React, { useState } from 'react';
import axios from 'axios';
import './UploadPage.css';

const UploadPage = () => {
    const [uploadStatus, setUploadStatus] = useState('');
    const [selectedFile, setSelectedFile] = useState(null);

    const handleFileChange = (e) => {
        setSelectedFile(e.target.files[0]);
    };

    const handleFileUpload = async() => {
        if (!selectedFile) {
            setUploadStatus('Please select a file to upload');
            return;
        }

        try {
            const formData = new FormData();
            formData.append('file', selectedFile);

            await axios.post('/api/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });

            setUploadStatus('File uploaded successfully');
            setSelectedFile(null);
        } catch (error) {
            console.error('Upload error:', error);

            const errorMessage =
                (error.response && error.response.data && error.response.data.message) ||
                error.message ||
                'An error occurred during upload';

            setUploadStatus(`Error uploading file: ${String(errorMessage)}`);
        }
    };

    return ( <
        div className = "upload-container" >
        <
        h1 > File Upload < /h1> <span><
        strong className = "red-text" > NOTE: < /strong> The file format should either be '.CSV'
        or 'JSON'. < /span ><p><span></span > < /p > <
        p className = "dis" > The file you have uploaded here will be uploaded to the data base. < /p><
        input type = "file"
        onChange = { handleFileChange }
        accept = ".csv,.json" /
        >
        <
        button className = "upload-button"
        onClick = { handleFileUpload }
        disabled = {!selectedFile } >
        Upload <
        /button> {
        uploadStatus && < p className = "upload-status" > { uploadStatus } < /p>} < /
        div >
    );
};

export default UploadPage;
