import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './VerticalNavBar.css';

function VerticalNavBar({ onFilterChange, onYearChange, toggleCompanyList, showCompanyList, schoolFilter, yearFilter, typeFilter, setSchoolFilter, setYearFilter, setTypeFilter }) {
    const [tooltip, setTooltip] = useState('');
    const [tooltipPosition, setTooltipPosition] = useState({ top: 0, left: 0 });

    const schools = ['SMEC', 'SCOPE', 'SENSE'];
    const types = [
        'Super dream internship',
        'Super dream offer',
        'Dream offer',
        'Dream internship',
        'Restricted dream'
    ];
    const years = [2023, 2024, 2025];

    const handleMouseEnter = (text, event) => {
        setTooltip(text);
        const { top, left, height } = event.currentTarget.getBoundingClientRect();
        setTooltipPosition({ top: top + height + window.scrollY, left });
    };

    const handleMouseLeave = () => setTooltip('');

    const handleFilterChange = (filterType, value) => {
        if (filterType === 'School') {
            setSchoolFilter(value);
        } else if (filterType === 'Year') {
            setYearFilter(value);
        } else if (filterType === 'Type') {
            setTypeFilter(value);
        }
    };

    return ( <
            div className = "vertical-nav" >
            <
            button onClick = {
                () => onFilterChange('All Students')
            } > All Students < /button> <
            button onClick = {
                () => onFilterChange('Placed')
            } > Placed Students < /button> <
            button onClick = {
                () => onFilterChange('Not placed')
            } > Not Placed Students < /button>

            <
            select value = { schoolFilter || "" }
            onChange = {
                (e) => handleFilterChange('School', e.target.value)
            }
            onMouseEnter = {
                (e) => handleMouseEnter("Filter by school.", e)
            }
            onMouseLeave = { handleMouseLeave } >
            <
            option value = "" > Select School < /option> {
            schools.map(school => ( < option key = { school }
                    value = { school } > { school } < /option>))} < /
                    select >

                    <
                    select value = { yearFilter || "" }
                    onChange = {
                        (e) => onYearChange(e.target.value)
                    }
                    onMouseEnter = {
                        (e) => handleMouseEnter("Filter by year.", e)
                    }
                    onMouseLeave = { handleMouseLeave } >
                    <
                    option value = "" > Select Year < /option> {
                    years.map(year => ( < option key = { year }
                            value = { year } > Year { year } < /option>))} < /
                            select >

                            <
                            select value = { typeFilter || "" }
                            onChange = {
                                (e) => handleFilterChange('Type', e.target.value)
                            }
                            onMouseEnter = {
                                (e) => handleMouseEnter("Filter by type of offer or internship.", e)
                            }
                            onMouseLeave = { handleMouseLeave } >
                            <
                            option value = "" > Select Type < /option> {
                            types.map(type => ( < option key = { type }
                                    value = { type } > { type } < /option>))} < /
                                    select >

                                    <
                                    button onClick = { toggleCompanyList } > { showCompanyList ? 'Hide Companies' : 'Companies Visited' } < /button> <
                                    Link to = "/job-notifications"
                                    className = "nav-link" > Job Notifications < /Link>

                                    {
                                        tooltip && ( <
                                            div className = "tooltip"
                                            style = {
                                                { top: tooltipPosition.top, left: tooltipPosition.left }
                                            } > { tooltip } < /div>
                                        )
                                    } <
                                    /div>
                                );
                            }

                            export default VerticalNavBar;
