import React, { useEffect, useState } from 'react';
import axios from 'axios';

const CompanyList = ({ year }) => {
        const [companies, setCompanies] = useState([]);
        const [loading, setLoading] = useState(false);
        const [error, setError] = useState(null);

        const fetchCompanies = async() => {
            if (!year) return;

            setLoading(true);
            setError(null);

            try {
                const response = await axios.get(`http://localhost:1000/api/companies?year=${year}`);
                setCompanies(response.data);
            } catch (error) {
                setError('Failed to fetch companies.');
                console.error(error);
            } finally {
                setLoading(false);
            }
        };

        useEffect(() => {
            fetchCompanies();
        }, [year]);

        return ( <
                div >
                <
                h2 > Companies Visited in { year } < /h2> {
                    loading && < p > Loading... < /p>} {
                        error && < p > { error } < /p>} {
                                companies.length > 0 ? ( <
                                    ul > {
                                        companies.map((company) => ( <
                                            li key = { company.name } > { company.name } - Placed: { company.placedCount } <
                                            /li>
                                        ))
                                    } <
                                    /ul>
                                ) : ( <
                                    p > No companies found
                                    for this year. < /p>
                                )
                            } <
                            /div>
                    );
                };

                export default CompanyList;
