import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import ChartDataLabels from 'chartjs-plugin-datalabels';
import { Chart as ChartJS, Title, Tooltip, Legend, BarElement, CategoryScale, LinearScale } from 'chart.js';

// Register the necessary components for Chart.js
ChartJS.register(Title, Tooltip, Legend, BarElement, CategoryScale, LinearScale);

const LessData = () => {
    const [placementData, setPlacementData] = useState([]);
    const [selectedYears, setSelectedYears] = useState([2023, 2024]); // Modify as needed

    useEffect(() => {
        // Fetch your placement data
        const fetchData = async() => {
            try {
                const response = await fetch('/api/your-endpoint'); // Update with your API endpoint
                const data = await response.json();
                setPlacementData(data);
                console.log('Fetched Placement Data:', data); // Debugging line
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    // Normalizes the status of the application
    const normalizeStatus = (status) => {
        switch (status) {
            case 'Applied':
                return 'Applied';
            case 'Shortlisted':
                return 'Shortlisted';
            case 'Placed':
                return 'Placed';
            case 'Not Placed':
                return 'Not Placed';
            default:
                return 'Unknown';
        }
    };

    const labels = [...new Set(placementData.map(item => normalizeStatus(item.ApplicationStatus)))];
    console.log('Labels:', labels); // Debugging line

    const chartData = {
        labels: labels,
        datasets: selectedYears.map((year, index) => {
            const yearData = placementData.filter(item => item.Year === parseInt(year));
            const counts = {};

            yearData.forEach(item => {
                const status = normalizeStatus(item.ApplicationStatus);
                counts[status] = (counts[status] || 0) + 1;
            });

            return {
                label: `${year} Placements`,
                data: labels.map(label => counts[label] || 0),
                backgroundColor: `rgba(${(index + 1) * 50}, ${(index + 1) * 100}, ${(index + 1) * 150}, 0.6)`,
                borderColor: `rgba(${(index + 1) * 50}, ${(index + 1) * 100}, ${(index + 1) * 150}, 1)`,
                borderWidth: 1,
            };
        }),
    };

    console.log('Chart Data:', chartData); // Debugging line

    const options = {
        responsive: true,
        plugins: {
            legend: {
                position: 'top',
            },
            datalabels: {
                anchor: 'end',
                align: 'end',
                formatter: (value) => value,
                color: 'white',
            },
        },
    };

    return ( <
        div className = "monitoring-chart" >
        <
        Bar data = { chartData }
        options = { options }
        plugins = {
            [ChartDataLabels]
        }
        /> < /
        div >
    );
};

export default LessData;
