import React, { useState, useEffect, useMemo } from 'react';
import StudentCard from './StudentCard';
import StudentDetailModal from './StudentDetailModal';
import './StudentList.css';
import { fetchStudents } from '../api/studentAPI';
import { groupByRegNo } from '../utils/groupByRegNo';

function StudentList({ filter, year, searchTerm, schoolFilter, typeFilter }) {
    const [students, setStudents] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedStudent, setSelectedStudent] = useState(null);

    useEffect(() => {
        const loadStudents = async() => {
            try {
                setLoading(true);
                // Fetch students based on all provided filters
                const data = await fetchStudents(filter, year, searchTerm, schoolFilter, typeFilter);
                console.log('Fetched student data:', data); // Log to verify the data

                if (Array.isArray(data)) {
                    // Group students by Reg_No after applying all filters
                    const groupedData = groupByRegNo(data);
                    setStudents(groupedData);
                } else {
                    console.error('Fetched data is not an array:', data);
                    setError('Unexpected data format from server.');
                }
            } catch (error) {
                console.error('Error loading students:', error);
                setError('Failed to load student data.');
            } finally {
                setLoading(false);
            }
        };

        loadStudents();
    }, [filter, year, searchTerm, schoolFilter, typeFilter]); // Trigger the effect whenever any of the filters change

    const handleStudentClick = (student) => {
        setSelectedStudent(student);
    };

    const closeModal = () => {
        setSelectedStudent(null);
    };

    // Memoize the student cards list to optimize re-rendering
    const studentCards = useMemo(() => {
        // Convert students object into an array and render the cards
        return Object.values(students).map((student) => ( <
            div key = { student.Reg_No }
            onClick = {
                () => handleStudentClick(student) } >
            <
            StudentCard student = { student }
            /> <
            /div>
        ));
    }, [students]); // Recalculate only when 'students' state changes

    if (loading) {
        return <p > Loading... < /p>;
    }

    if (error) {
        return <p className = "error-message" > { error } < /p>;
    }

    return ( <
        div className = "student-list" >
        <
        h2 style = {
            { marginLeft: '20px', fontSize: '30px' } } > Students < /h2> <
        div className = "student-cards-container" > {
            Object.keys(students).length === 0 ? ( <
                p > No students found matching this criteria. < /p>
            ) : (
                studentCards // Display the memoized student cards
            )
        } <
        /div>

        {
            selectedStudent && ( <
                StudentDetailModal student = { selectedStudent }
                onClose = { closeModal }
                />
            )
        } <
        /div>
    );
}

export default StudentList;
