import React, { useState, useEffect, useMemo } from 'react';
import StudentCard from './StudentCard';
import StudentDetailModal from './StudentDetailModal';
import './StudentList.css';
import { fetchStudents } from '../api/studentAPI';
import { groupByRegNo } from '../utils/groupByRegNo';

function StudentList({ filter, year, searchTerm, schoolFilter, typeFilter }) {
    const [students, setStudents] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedStudent, setSelectedStudent] = useState(null);

    useEffect(() => {
        const loadStudents = async() => {
            setLoading(true);
            setError(null);

            try {
                const data = await fetchStudents(filter, year, searchTerm, schoolFilter, typeFilter);
                console.log("Fetched data:", data);

                if (Array.isArray(data)) {
                    const groupedData = groupByRegNo(data);

                    // Filter students based on schoolFilter and typeFilter
                    const filteredStudents = Object.values(groupedData).filter(studentGroup => {
                        const firstStudent = studentGroup[0]; // Assuming all students in a group have the same school and type
                        const matchesSchool = schoolFilter ?
                            firstStudent.School && firstStudent.School.toLowerCase() === schoolFilter.toLowerCase() :
                            true;

                        const matchesType = typeFilter ?
                            firstStudent.Type && firstStudent.Type.toLowerCase() === typeFilter.toLowerCase() :
                            true;

                        return matchesSchool && matchesType;
                    });

                    setStudents(filteredStudents);
                } else {
                    console.error('Fetched data is not an array:', data);
                    setStudents([]);
                    setError('Unexpected data format from server.');
                }
            } catch (error) {
                console.error('Error loading students:', error);
                setError('Failed to load student data.');
            } finally {
                setLoading(false);
            }
        };

        loadStudents();
    }, [filter, year, searchTerm, schoolFilter, typeFilter]); // Added schoolFilter and typeFilter as dependencies

    const handleStudentClick = (student) => {
        setSelectedStudent(student);
    };

    const closeModal = () => {
        setSelectedStudent(null);
    };

    const studentCards = useMemo(() => {
        return students.map(student => ( <
            div key = { student.Reg_No }
            onClick = {
                () => handleStudentClick(student)
            } >
            <
            StudentCard student = { student }
            /> < /
            div >
        ));
    }, [students]);

    if (loading) {
        return <p > Loading... < /p>;
    }

    if (error) {
        return <p className = "error-message" > { error } < /p>;
    }

    return ( <
            div className = "student-list" >
            <
            h2 style = {
                { marginLeft: '20px', fontSize: '30px' }
            } > Students < /h2> { } <div className="button-container" style={{ marginLeft: '20px', marginBottom: '20px' }}> <
            button className = "student-filter-button" > Scope < /button> <
            button className = "student-filter-button" > Sense < /button> <
            button className = "student-filter-button" > SMEC < /button> < /
            div >
            <
            div className = "student-cards-container" > {
                students.length === 0 ? ( <
                    p > No students found matching this criteria. < /p>
                ) : (
                    studentCards
                )
            } <
            /div> {
            selectedStudent && ( <
                StudentDetailModal student = { selectedStudent }
                onClose = { closeModal }
                />
            )
        } <
        /div>
);
}

export default StudentList;
