import React, { useState, useEffect } from 'react';
import StudentCard from './StudentCard';
import StudentDetailModal from './StudentDetailModal';
import './StudentList.css';
import { fetchStudents } from '../api/studentAPI';
import { groupByRegNo } from '../utils/groupByRegNo';

function StudentList({ filter, year, searchTerm, schoolFilter, typeFilter }) {
    const [students, setStudents] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedStudent, setSelectedStudent] = useState(null);

    useEffect(() => {
        const loadStudents = async() => {
            try {
                setLoading(true);
                const data = await fetchStudents(filter, year, searchTerm);

                if (Array.isArray(data)) {
                    const groupedData = groupByRegNo(data);
                    // Convert the grouped object to an array
                    setStudents(Object.values(groupedData)); // Values are now arrays of students
                } else {
                    console.error('Fetched data is not an array:', data);
                    setError('Unexpected data format from server.');
                }
            } catch (error) {
                console.error('Error loading students:', error);
                setError('Failed to load student data.');
            } finally {
                setLoading(false);
            }
        };

        loadStudents();
    }, [filter, year, searchTerm]);

    const handleStudentClick = (student) => {
        setSelectedStudent(student);
    };

    const closeModal = () => {
        setSelectedStudent(null);
    };

    // Apply the filters only if filter is not "All Students"
    const filteredStudents = students
        .map(group => {
            // Ensure that group is an array before applying filter
            if (Array.isArray(group)) {
                return group.filter(student => {
                    // Apply filters for school and type only if filter is not "All Students"
                    if (filter !== 'All Students') {
                        const matchesSchool = schoolFilter ?
                            student.School && student.School.toLowerCase().includes(schoolFilter.toLowerCase()) :
                            true;
                        const matchesType = typeFilter ?
                            student.Type && student.Type.toLowerCase().includes(typeFilter.toLowerCase()) :
                            true;
                        return matchesSchool && matchesType;
                    }
                    return true; // If "All Students", don't apply any filters
                });
            }
            return []; // If group is not an array, return an empty array
        })
        .filter(group => group.length > 0); // Remove empty groups

    if (loading) {
        return <p > Loading... < /p>;
    }

    if (error) {
        return <p className = "error-message" > { error } < /p>;
    }

    return ( <
        div className = "student-list" >
        <
        h2 style = {
            { marginLeft: '20px', fontSize: '30px' } } > Students < /h2> <
        div className = "student-cards-container" > {
            filteredStudents.length > 0 ? (
                filteredStudents.map((group, index) =>
                    group.map((student) => ( <
                        div onClick = {
                            () => handleStudentClick(student) }
                        key = { student.Reg_No } >
                        <
                        StudentCard student = { student }
                        /> <
                        /div>
                    ))
                )
            ) : ( <
                p > No students found matching this criteria. < /p>
            )
        } <
        /div> {
            selectedStudent && ( <
                StudentDetailModal student = { selectedStudent }
                onClose = { closeModal }
                />
            )
        } <
        /div>
    );
}

export default StudentList;
