// src/components/CTCChart.js
import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2'; // Import Line chart instead of Bar chart
import axios from 'axios';

const CTCChart = () => {
    const [chartData, setChartData] = useState(null);

    useEffect(() => {
        // Fetch data from the backend
        axios.get('/api/students/ctc-by-school-year')
            .then(response => {
                const data = response.data;

                // Get unique schools and years for dynamic labels and colors
                const schools = [...new Set(data.map(item => item.school))];
                const years = [...new Set(data.map(item => item.year))].sort();

                // Create a color for each school
                const colors = schools.map((_, index) => `hsl(${(index * 360) / schools.length}, 70%, 50%)`);

                // Organize data by year and school
                const datasets = schools.map((school, index) => {
                    const schoolData = years.map(year => {
                        const item = data.find(d => d.school === school && d.year === year);
                        return item ? item.avgCTC : null; // null if no data for that year-school combo
                    });

                    return {
                        label: school,
                        data: schoolData,
                        backgroundColor: 'transparent', // no background for lines
                        borderColor: colors[index], // color for lines
                        borderWidth: 2, // width of the lines
                        fill: false, // no fill below the lines
                        tension: 0.4 // smooth the line curve
                    };
                });

                // Update chart data
                setChartData({
                    labels: years,
                    datasets
                });
            })
            .catch(error => console.error("Error fetching data:", error));
    }, []);

    return ( <
        div >
        <
        h2 > Average CTC by School and Year < /h2> {
            chartData ? ( <
                Line data = { chartData } // Use Line instead of Bar
                options = {
                    {
                        scales: {
                            x: {
                                title: {
                                    display: true,
                                    text: 'Year'
                                }
                            },
                            y: {
                                beginAtZero: true,
                                title: {
                                    display: true,
                                    text: 'CTC in LPA'
                                }
                            }
                        },
                        plugins: {
                            legend: {
                                position: 'top'
                            }
                        }
                    }
                }
                />
            ) : ( <
                p > Loading chart data... < /p>
            )
        } <
        /div>
    );
};

export default CTCChart;
