import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSquarePen, faTrashCan, faCircleXmark } from '@fortawesome/free-solid-svg-icons';
import './JobNotifications.css';

const JobNotifications = () => {
    const [notifications, setNotifications] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [showDetailModal, setShowDetailModal] = useState(false);
    const [newNotification, setNewNotification] = useState({
        company: '',
        category: '',
        date: '',
        eligibleBranches: '',
        eligibleCriteria: '',
        ctc: '',
        website: '',
        jobLocation: '',
        description: ''
    });
    const [selectedNotification, setSelectedNotification] = useState(null);

    useEffect(() => {
        const storedNotifications = JSON.parse(localStorage.getItem('jobNotifications')) || [];
        setNotifications(storedNotifications);
    }, []);

    useEffect(() => {
        localStorage.setItem('jobNotifications', JSON.stringify(notifications));
    }, [notifications]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setNewNotification(prev => ({...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (newNotification.company && newNotification.category) { // Validation check
            setNotifications(prev => [
                ...prev,
                {...newNotification, id: prev.length + 1 }
            ]);
            setNewNotification({
                company: '',
                category: '',
                date: '',
                eligibleBranches: '',
                eligibleCriteria: '',
                ctc: '',
                website: '',
                jobLocation: '',
                description: ''
            });
            setShowModal(false);
        }
    };

    const handleCardClick = (notification) => {
        setSelectedNotification(notification);
        setShowDetailModal(true);
    };

    const handleRemoveNotification = (id) => {
        setNotifications(prev => prev.filter(notification => notification.id !== id));
    };

    const closeDetailModal = () => {
        setShowDetailModal(false);
        setSelectedNotification(null);
    };

    const closeUploadModal = () => {
        setShowModal(false);
    };

    // Helper function to categorize notifications
    const categorizeNotifications = () => {
        const today = new Date();
        const upcoming = notifications.filter(notification => new Date(notification.date) >= today);
        const completed = notifications.filter(notification => new Date(notification.date) < today);
        return { upcoming, completed };
    };

    const { upcoming, completed } = categorizeNotifications();

    return ( <
        div className = "job-notifications" >
        <
        h1 > Job Notifications < /h1> <
        div className = "upload-button-wrapper" >
        <
        button className = "upload-button_1"
        onClick = {
            () => setShowModal(true) } >
        <
        FontAwesomeIcon icon = { faSquarePen }
        /> <
        /button> <
        /div>

        <
        h2 > Upcoming Notifications < /h2> <
        div className = "cards-container" > {
            upcoming.map(notification => ( <
                div key = { notification.id }
                className = "job-card"
                onClick = {
                    () => handleCardClick(notification) } >
                <
                button className = "remove-buttonn"
                onClick = {
                    (e) => {
                        e.stopPropagation();
                        handleRemoveNotification(notification.id);
                    }
                } >
                <
                FontAwesomeIcon icon = { faTrashCan }
                /> <
                /button> <
                h3 > { notification.company } < /h3> <
                p > Date of Visit: { notification.date } < /p> <
                p > Category: { notification.category } < /p> <
                /div>
            ))
        } <
        /div>

        <
        h2 > Completed Notifications < /h2> <
        div className = "cards-container" > {
            completed.map(notification => ( <
                div key = { notification.id }
                className = "job-card"
                onClick = {
                    () => handleCardClick(notification) } >
                <
                button className = "remove-buttonn"
                onClick = {
                    (e) => {
                        e.stopPropagation();
                        handleRemoveNotification(notification.id);
                    }
                } >
                <
                FontAwesomeIcon icon = { faTrashCan }
                /> <
                /button> <
                h3 > { notification.company } < /h3> <
                p > Date of Visit: { notification.date } < /p> <
                p > Category: { notification.category } < /p> <
                /div>
            ))
        } <
        /div>

        {
            showModal && ( <
                div className = "modal" >
                <
                div className = "modal-content" >
                <
                FontAwesomeIcon icon = { faCircleXmark }
                onClick = { closeUploadModal }
                className = "close-icon" / >
                <
                h2 > Upload Job Notification < /h2> <
                form onSubmit = { handleSubmit } >
                <
                label > Company:
                <
                input type = "text"
                name = "company"
                value = { newNotification.company }
                onChange = { handleInputChange }
                required placeholder = "Enter company name" / >
                <
                /label> <
                label > Category:
                <
                input type = "text"
                name = "category"
                value = { newNotification.category }
                onChange = { handleInputChange }
                required placeholder = "Enter job category" / >
                <
                /label> <
                label > Date of Visit:
                <
                input type = "date"
                name = "date"
                value = { newNotification.date }
                onChange = { handleInputChange }
                required / >
                <
                /label> <
                label > Eligible Branches:
                <
                input type = "text"
                name = "eligibleBranches"
                value = { newNotification.eligibleBranches }
                onChange = { handleInputChange }
                required placeholder = "Enter eligible branches" / >
                <
                /label> <
                label > Eligible Criteria:
                <
                input type = "text"
                name = "eligibleCriteria"
                value = { newNotification.eligibleCriteria }
                onChange = { handleInputChange }
                required placeholder = "Enter eligible criteria" / >
                <
                /label> <
                label > CTC:
                <
                input type = "text"
                name = "ctc"
                value = { newNotification.ctc }
                onChange = { handleInputChange }
                required placeholder = "Enter CTC" / >
                <
                /label> <
                label > Website:
                <
                input type = "url"
                name = "website"
                value = { newNotification.website }
                onChange = { handleInputChange }
                placeholder = "Enter company website" / >
                <
                /label> <
                label > Job Location:
                <
                input type = "text"
                name = "jobLocation"
                value = { newNotification.jobLocation }
                onChange = { handleInputChange }
                required placeholder = "Enter job location" / >
                <
                /label> <
                label > Description:
                <
                textarea name = "description"
                value = { newNotification.description }
                onChange = { handleInputChange }
                required placeholder = "Enter job description" > < /textarea> <
                /label> <
                button type = "submit"
                className = "close-button_1" > Upload < /button> <
                /form> <
                /div> <
                /div>
            )
        }

        {
            showDetailModal && selectedNotification && ( <
                div className = "modal" >
                <
                div className = "modal-content" >
                <
                FontAwesomeIcon icon = { faCircleXmark }
                onClick = { closeDetailModal }
                className = "close-icon" / >
                <
                h2 > { selectedNotification.company } < /h2> <
                p > < strong > Category: < /strong>{selectedNotification.category}</p >
                <
                p > < strong > Date of Visit: < /strong>{selectedNotification.date}</p >
                <
                p > < strong > Eligible Branches: < /strong>{selectedNotification.eligibleBranches}</p >
                <
                p > < strong > Eligible Criteria: < /strong>{selectedNotification.eligibleCriteria}</p >
                <
                p > < strong > CTC: < /strong>{selectedNotification.ctc}</p >
                <
                p > < strong > Website: < /strong>{selectedNotification.website}</p >
                <
                p > < strong > Job Location: < /strong>{selectedNotification.jobLocation}</p >
                <
                p > < strong > Description: < /strong>{selectedNotification.description}</p >
                <
                /div> <
                /div>
            )
        } <
        /div>
    );
};

export default JobNotifications;
