import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom'; // Import Link for navigation
import axios from 'axios'; // Import axios for API calls
import './VerticalNavBar.css';

function VerticalNavBar({ onFilterChange, onYearChange, toggleCompanyList, showCompanyList }) {
    const [tooltip, setTooltip] = useState('');
    const [tooltipPosition, setTooltipPosition] = useState({ top: 0, left: 0 });
    const [schools, setSchools] = useState([]); // State to store schools
    const [types, setTypes] = useState([]); // State to store types
    const [years, setYears] = useState([]); // State to store years

    useEffect(() => {
        // Fetch school, type, and year data from the backend
        const fetchData = async() => {
            try {
                // Make API calls to get distinct values for schools, types, and years
                const responseSchools = await axios.get('/api/schools'); // Fetch schools from backend
                const responseTypes = await axios.get('/api/types'); // Fetch types from backend
                const responseYears = await axios.get('/api/years'); // Fetch years from backend

                setSchools(responseSchools.data); // Store schools in state
                setTypes(responseTypes.data); // Store types in state
                setYears(responseYears.data); // Store years in state
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData(); // Call the fetchData function to fetch data on component mount
    }, []);

    const handleMouseEnter = (text, event) => {
        setTooltip(text);
        const { top, left, height } = event.currentTarget.getBoundingClientRect();
        setTooltipPosition({ top: top + height + window.scrollY, left: left + window.scrollX });
    };

    const handleMouseLeave = () => {
        setTooltip('');
    };

    return ( <
            div className = "vertical-nav" >
            <
            button onClick = {
                () => onFilterChange('All Students')
            }
            onMouseEnter = {
                (e) => handleMouseEnter("View all students regardless of placement status.", e)
            }
            onMouseLeave = { handleMouseLeave } >
            All Students <
            /button>

            <
            button onClick = {
                () => onFilterChange('Placed')
            }
            onMouseEnter = {
                (e) => handleMouseEnter("View only the students who have been placed.", e)
            }
            onMouseLeave = { handleMouseLeave } >
            Placed Students <
            /button>

            <
            button onClick = {
                () => onFilterChange('Not placed')
            }
            onMouseEnter = {
                (e) => handleMouseEnter("View students who have not been placed.", e)
            }
            onMouseLeave = { handleMouseLeave } >
            Not Placed Students <
            /button>

            { /* School Dropdown */ } <
            select onChange = {
                (e) => console.log(e.target.value)
            } // Implement filter logic
            onMouseEnter = {
                (e) => handleMouseEnter("Select a school.", e)
            }
            onMouseLeave = { handleMouseLeave } >
            <
            option value = "" > Select School < /option> {
            schools.map((school) => ( <
                option key = { school }
                value = { school } > { school } < /option>
            ))
        } <
        /select>

    { /* Year Dropdown */ } <
    select onChange = {
        (e) => onYearChange(e.target.value)
    }
    onMouseEnter = {
        (e) => handleMouseEnter("Select a year to filter students by their application year.", e)
    }
    onMouseLeave = { handleMouseLeave } >
        <
        option value = "" > Select Year < /option> {
    years.map((year) => ( <
        option key = { year }
        value = { year } > Year { year } < /option>
    ))
} <
/select>

{ /* Type Dropdown */ } <
select onChange = {
        (e) => console.log(e.target.value)
    } // Implement filter logic
onMouseEnter = {
    (e) => handleMouseEnter("Select a type.", e)
}
onMouseLeave = { handleMouseLeave } >
    <
    option value = "" > Select Type < /option> {
types.map((type) => ( <
    option key = { Type }
    value = { type } > { type } < /option>
))
} <
/select>

{ /* Companies Visited Button */ } <
button onClick = { toggleCompanyList }
onMouseEnter = {
    (e) => handleMouseEnter(showCompanyList ? "Hide the list of companies visited." : "Show the list of companies visited.", e)
}
onMouseLeave = { handleMouseLeave } > { showCompanyList ? 'Hide Companies' : 'Companies Visited' } <
    /button>

{ /* Job Notifications Link */ } <
Link to = "/job-notifications"
className = "nav-link"
onMouseEnter = {
    (e) => handleMouseEnter("View job notifications.", e)
}
onMouseLeave = { handleMouseLeave } >
    Job Notifications <
    /Link>

{
    tooltip && ( <
        div className = "tooltip"
        style = {
            { top: tooltipPosition.top, left: tooltipPosition.left }
        } > { tooltip } <
        /div>
    )
} <
/div>
);
}

export default VerticalNavBar;
