import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './VerticalNavBar.css';

function VerticalNavBar({ onFilterChange, onYearChange, toggleCompanyList, showCompanyList }) {
    const [tooltip, setTooltip] = useState('');
    const [tooltipPosition, setTooltipPosition] = useState({ top: 0, left: 0 });

    const schools = ['SMEC', 'SCOPE', 'SENSE'];
    const types = [
        'Super dream internship',
        'Super dream offer',
        'Dream offer',
        'Dream internship',
        'Restricted dream'
    ];
    const years = [2023, 2024, 2025];

    const handleMouseEnter = (text, event) => {
        setTooltip(text);
        const { top, left, height } = event.currentTarget.getBoundingClientRect();
        setTooltipPosition({ top: top + height + window.scrollY, left });
    };

    const handleMouseLeave = () => setTooltip('');

    return ( <
        div className = "vertical-nav" >
        <
        button onClick = {
            () => onFilterChange({ status: 'All Students' }) }
        onMouseEnter = {
            (e) => handleMouseEnter("View all students.", e) }
        onMouseLeave = { handleMouseLeave } >
        All Students <
        /button>

        <
        button onClick = {
            () => onFilterChange({ status: 'Placed' }) }
        onMouseEnter = {
            (e) => handleMouseEnter("View students who have been placed.", e) }
        onMouseLeave = { handleMouseLeave } >
        Placed Students <
        /button>

        <
        button onClick = {
            () => onFilterChange({ status: 'Not placed' }) }
        onMouseEnter = {
            (e) => handleMouseEnter("View students who are not placed.", e) }
        onMouseLeave = { handleMouseLeave } >
        Not Placed Students <
        /button>

        <
        select onChange = {
            (e) => onFilterChange({ school: e.target.value }) }
        onMouseEnter = {
            (e) => handleMouseEnter("Filter by school.", e) }
        onMouseLeave = { handleMouseLeave } >
        <
        option value = "" > Select School < /option> {
            schools.map(school => ( <
                option key = { school }
                value = { school } > { school } < /option>
            ))
        } <
        /select>

        <
        select onChange = {
            (e) => onYearChange(e.target.value) }
        onMouseEnter = {
            (e) => handleMouseEnter("Filter by year.", e) }
        onMouseLeave = { handleMouseLeave } >
        <
        option value = "" > Select Year < /option> {
            years.map(year => ( <
                option key = { year }
                value = { year } > Year { year } < /option>
            ))
        } <
        /select>

        <
        select onChange = {
            (e) => onFilterChange({ type: e.target.value }) }
        onMouseEnter = {
            (e) => handleMouseEnter("Filter by type of offer or internship.", e) }
        onMouseLeave = { handleMouseLeave } >
        <
        option value = "" > Select Type < /option> {
            types.map(type => ( <
                option key = { type }
                value = { type } > { type } < /option>
            ))
        } <
        /select>

        <
        button onClick = { toggleCompanyList }
        onMouseEnter = {
            (e) => handleMouseEnter(showCompanyList ? "Hide company list." : "Show company list.", e) }
        onMouseLeave = { handleMouseLeave } > { showCompanyList ? 'Hide Companies' : 'Companies Visited' } <
        /button>

        <
        Link to = "/job-notifications"
        className = "nav-link"
        onMouseEnter = {
            (e) => handleMouseEnter("View job notifications.", e) }
        onMouseLeave = { handleMouseLeave } >
        Job Notifications <
        /Link>

        {
            tooltip && ( <
                div className = "tooltip"
                style = {
                    { top: tooltipPosition.top, left: tooltipPosition.left } } > { tooltip } <
                /div>
            )
        } <
        /div>
    );
}

export default VerticalNavBar;
