import React, { useState } from 'react';
import './VerticalNavBar.css';

function VerticalNavBar({ onFilterChange, onYearChange, toggleCompanyList, showCompanyList }) {
    const [hoveredButton, setHoveredButton] = useState('');

    const handleMouseEnter = (button) => {
        setHoveredButton(button);
    };

    const handleMouseLeave = () => {
        setHoveredButton('');
    };

    const renderTooltip = () => {
        switch (hoveredButton) {
            case 'all':
                return "View all students regardless of placement status.";
            case 'placed':
                return "View only the students who have been placed.";
            case 'notPlaced':
                return "View students who have not been placed.";
            case 'year':
                return "Select a year to filter students by their application year.";
            case 'companies':
                return showCompanyList ?
                    "Hide the list of companies visited." :
                    "Show the list of companies visited.";
            default:
                return '';
        }
    };

    return ( <
        div className = "vertical-nav" >
        <
        button onClick = {
            () => onFilterChange('All Students') }
        onMouseEnter = {
            () => handleMouseEnter('all') }
        onMouseLeave = { handleMouseLeave } >
        All Students <
        /button>

        <
        button onClick = {
            () => onFilterChange('Placed') }
        onMouseEnter = {
            () => handleMouseEnter('placed') }
        onMouseLeave = { handleMouseLeave } >
        Placed Students <
        /button>

        <
        button onClick = {
            () => onFilterChange('Not Placed') }
        onMouseEnter = {
            () => handleMouseEnter('notPlaced') }
        onMouseLeave = { handleMouseLeave } >
        Not Placed Students <
        /button>

        <
        select onChange = { e => onYearChange(e.target.value) }
        onMouseEnter = {
            () => handleMouseEnter('year') }
        onMouseLeave = { handleMouseLeave } >
        <
        option value = "" > Select Year < /option> <
        option value = "2022" > Year 2022 < /option> <
        option value = "2023" > Year 2023 < /option> <
        option value = "2024" > Year 2024 < /option> <
        option value = "2025" > Year 2025 < /option> <
        option value = "2026" > Year 2026 < /option> <
        /select>

        <
        button onClick = { toggleCompanyList }
        onMouseEnter = {
            () => handleMouseEnter('companies') }
        onMouseLeave = { handleMouseLeave } > { showCompanyList ? 'Hide Companies' : 'Companies Visited' } <
        /button>

        {
            hoveredButton && ( <
                div className = "tooltip"
                style = {
                    {
                        left: '110%', // Position the tooltip to the right of the button
                        top: `${(hoveredButton === 'year' ? '30px' : '50%')}`, // Adjust vertical position based on the hovered element
                    }
                } > { renderTooltip() } <
                /div>
            )
        } <
        /div>
    );
}

export default VerticalNavBar;
