import React, { useState } from 'react';
import './VerticalNavBar.css';

function VerticalNavBar({ onFilterChange, onYearChange, onSchoolChange, schoolFilter, typeFilter, yearFilter }) {
    const [tooltip, setTooltip] = useState('');
    const [tooltipPosition, setTooltipPosition] = useState({ top: 0, left: 0 });
    const [schools] = useState(['SMEC', 'SCOPE', 'SENSE']); // Static list of schools
    const [types] = useState(['Super dream internship', 'Super dream offer', 'Dream offer', 'Dream internship', 'Restricted dream']); // Static list of types
    const [years] = useState([2023, 2024, 2025]); // Example years
    const [loading] = useState(false); // Static loading state

    const handleTypeChange = (event) => {
        const selectedType = event.target.value;
        onFilterChange('Type', selectedType); // Pass selected type filter to the parent component
    };

    const handleYearChange = (event) => {
        const selectedYear = event.target.value;
        onYearChange(selectedYear); // Trigger onYearChange with the selected year
    };

    if (loading) {
        return <div > Loading... < /div>;
    }

    return ( <
        div className = "vertical-nav" > { /* Buttons for Filters */ } <
        button onClick = {
            () => onFilterChange('All Students') }
        className = { typeFilter === 'All Students' ? 'active' : '' } >
        All Students <
        /button> <
        button onClick = {
            () => onFilterChange('Placed') }
        className = { typeFilter === 'Placed' ? 'active' : '' } >
        Placed Students <
        /button> <
        button onClick = {
            () => onFilterChange('Not placed') }
        className = { typeFilter === 'Not placed' ? 'active' : '' } >
        Not Placed Students <
        /button>

        { /* School Filter */ } <
        select onChange = { onSchoolChange }
        value = { schoolFilter } >
        <
        option value = "" > Select School < /option> {
            schools.map((school) => ( <
                option key = { school }
                value = { school } > { school } <
                /option>
            ))
        } <
        /select>

        { /* Year Filter */ } <
        select onChange = { handleYearChange }
        value = { yearFilter } >
        <
        option value = "" > Select Year < /option> {
            years.map((year) => ( <
                option key = { year }
                value = { year } >
                Year { year } <
                /option>
            ))
        } <
        /select>

        { /* Type Filter */ } <
        select onChange = { handleTypeChange }
        value = { typeFilter } >
        <
        option value = "" > Select Type < /option> {
            types.map((type) => ( <
                option key = { type }
                value = { type } > { type } <
                /option>
            ))
        } <
        /select> <
        /div>
    );
}

export default VerticalNavBar;
