import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './Navbar.css';
import logo from './logo.png';

function Navbar({ onSearch }) {
    const [searchTerm, setSearchTerm] = useState('');
    const [isDarkMode, setIsDarkMode] = useState(false); // State for dark mode
    const navigate = useNavigate();
    const location = useLocation();
    const isLoggedIn = !!localStorage.getItem('authToken');

    useEffect(() => {
        // Check user's preference from localStorage
        const savedMode = localStorage.getItem('theme') || 'light';
        setIsDarkMode(savedMode === 'dark');
        document.body.className = savedMode; // Set the initial theme
    }, []);

    const handleSearchChange = (e) => {
        const value = e.target.value;
        setSearchTerm(value);
        if (onSearch) {
            onSearch(value);
        }
    };

    const handleHomeClick = (e) => {
        e.preventDefault();
        navigate('/');
    };

    const handleAboutClick = (e) => {
        e.preventDefault();
        navigate('/about');
    };

    const handleContactClick = (e) => {
        e.preventDefault();
        navigate('/contact');
    };

    const handleLogout = () => {
        localStorage.removeItem('authToken');
        navigate('/login');
    };

    const toggleMode = () => {
        const newMode = isDarkMode ? 'light' : 'dark';
        setIsDarkMode(!isDarkMode);
        document.body.className = newMode; // Toggle the class on the body
        localStorage.setItem('theme', newMode); // Save the preference
    };

    return ( <
        nav className = { `navbar ${isDarkMode ? 'dark' : 'light'}` } >
        <
        img src = { logo }
        alt = "Logo"
        className = "navbar-logo" / >
        <
        ul className = "navbar-menu" > {
            isLoggedIn && ( <
                >
                <
                li >
                <
                a href = "/"
                onClick = { handleHomeClick }
                className = { location.pathname === '/' ? 'active' : '' } >
                Home <
                /a> <
                /li> <
                li >
                <
                a href = "/about"
                onClick = { handleAboutClick }
                className = { location.pathname === '/about' ? 'active' : '' } >
                About <
                /a> <
                /li> <
                li >
                <
                a href = "/contact"
                onClick = { handleContactClick }
                className = { location.pathname === '/contact' ? 'active' : '' } >
                Contact <
                /a> <
                /li> <
                />
            )
        } <
        /ul> <
        div className = "navbar-search" >
        <
        input type = "text"
        placeholder = "Search by Registration Number"
        value = { searchTerm }
        onChange = { handleSearchChange }
        /> <
        /div> {
            isLoggedIn && ( <
                div className = "button-group" > { /* Grouping buttons */ } <
                button className = "mode-toggle-button"
                onClick = { toggleMode } > { isDarkMode ? 'Light Mode' : 'Dark Mode' } <
                /button> <
                button className = "logout-button"
                onClick = { handleLogout } >
                Logout <
                /button> <
                /div>
            )
        } <
        /nav>
    );
}

export default Navbar;
