import React, { useState, useEffect } from 'react';
import StudentCard from './StudentCard';
import StudentDetailModal from './StudentDetailModal';
import './StudentList.css';
import { fetchStudents } from '../api/studentAPI';
import { groupByRegNo } from '../utils/groupByRegNo';

function StudentList({ filter, year, searchTerm }) {
    const [students, setStudents] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedStudent, setSelectedStudent] = useState(null);
    const [schoolFilter, setSchoolFilter] = useState(''); // State for school filter

    useEffect(() => {
        const loadStudents = async() => {
            setLoading(true);
            setError(null);

            try {
                // Fetch data with current filters
                const data = await fetchStudents(filter, year, searchTerm, schoolFilter);
                console.log("Fetched data:", data); // Log fetched data to inspect its structure

                if (Array.isArray(data)) {
                    // Group the students by registration number
                    const groupedData = groupByRegNo(data);

                    // Log grouped data to verify structure after grouping by RegNo
                    console.log("Grouped data:", groupedData);

                    // Filter students based on schoolFilter
                    const filteredStudents = Object.values(groupedData).filter(studentGroup => {
                        return studentGroup.some(student => {
                            // Check if the school matches the filter (case-insensitive)
                            return schoolFilter ?
                                (student.School && student.School.toLowerCase() === schoolFilter.toLowerCase()) :
                                true;
                        });
                    });

                    console.log("Filtered students:", filteredStudents); // Log filtered students
                    setStudents(filteredStudents);
                } else {
                    console.error('Fetched data is not an array:', data);
                    setStudents([]);
                    setError('Unexpected data format from server.');
                }
            } catch (error) {
                console.error('Error loading students:', error);
                setError('Failed to load student data.');
            } finally {
                setLoading(false);
            }
        };

        loadStudents();
    }, [filter, year, searchTerm, schoolFilter]);

    const handleStudentClick = (student) => {
        setSelectedStudent(student);
    };

    const closeModal = () => {
        setSelectedStudent(null);
    };

    const studentCards = students.map(student => ( <
        div key = { student.Reg_No }
        onClick = {
            () => handleStudentClick(student)
        } >
        <
        StudentCard student = { student }
        /> < /
        div >
    ));

    const handleSchoolFilterChange = (school) => {
        setSchoolFilter(school); // Update the school filter directly
    };

    const clearSchoolFilter = () => {
        setSchoolFilter(''); // Clear the filter
    };

    if (loading) {
        return <div className = "loading-spinner" > Loading... < /div>;
    }

    if (error) {
        return <p className = "error-message" > { error } < /p>;
    }

    return ( <
        div className = "student-list" >
        <
        h2 style = {
            { marginLeft: '20px', fontSize: '30px' }
        } > Students < /h2>

        { /* Buttons for Scope, Sense, and SMEC */ } <
        div className = "button-container"
        style = {
            { marginLeft: '20px', marginBottom: '20px' }
        } >
        <
        button className = "student-filter-button"
        onClick = {
            () => handleSchoolFilterChange('SCOPE')
        } > SCOPE < /button> <
        button className = "student-filter-button"
        onClick = {
            () => handleSchoolFilterChange('SENSE')
        } > SENSE < /button> <
        button className = "student-filter-button"
        onClick = {
            () => handleSchoolFilterChange('SMEC')
        } > SMEC < /button> <
        button className = "student-filter-button"
        onClick = { clearSchoolFilter } > Clear Filter < /button> < /
        div >

        <
        div className = "student-cards-container" > {
            students.length === 0 ? ( <
                p > No students found matching this criteria. < /p>
            ) : (
                studentCards
            )
        } <
        /div>

        {
            selectedStudent && ( <
                StudentDetailModal student = { selectedStudent }
                onClose = { closeModal }
                />
            )
        } <
        /div>
    );
}

export default StudentList;
