import axios from 'axios';

// api/studentAPI.js
// api/studentAPI.js
export const fetchStudents = async(filter, year, searchTerm, schoolFilter, typeFilter) => {
    try {
        let url = '/api/students';
        const params = new URLSearchParams();

        if (filter) params.append('filter', filter);
        if (year) params.append('year', year);
        if (searchTerm) params.append('searchTerm', searchTerm);
        if (schoolFilter) params.append('schoolFilter', schoolFilter); // Ensure 'schoolFilter' is passed correctly
        if (typeFilter) params.append('typeFilter', typeFilter); // Ensure 'typeFilter' is passed correctly

        url += `?${params.toString()}`;
        const response = await axios.get(url);
        return response.data;
    } catch (error) {
        console.error('Error fetching students:', error);
        throw error;
    }
};
