import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './UploadPage.css';

const UploadPage = () => {
    const [uploadStatus, setUploadStatus] = useState('');
    const [statusType, setStatusType] = useState(''); // 'success' or 'error'
    const [selectedFile, setSelectedFile] = useState(null);
    const [recentUploads, setRecentUploads] = useState([]); // Initialize as an empty array
    const [selectedDateFilter, setSelectedDateFilter] = useState(''); // Filter date

    // Fetch recent uploads when the component mounts
    useEffect(() => {
        fetchRecentUploads();
    }, []);

    // Fetch the recent uploads from the backend
    const fetchRecentUploads = async() => {
        try {
            const response = await axios.get('/api/recent-uploads'); // Backend endpoint to fetch recent uploads
            if (response.data.uploads) {
                setRecentUploads(response.data.uploads); // Assuming response contains a list of uploads
            } else {
                setRecentUploads([]); // Set to empty array if no uploads are found
            }
        } catch (error) {
            console.error('Error fetching recent uploads:', error);
            setRecentUploads([]); // Set to empty array in case of an error
        }
    };

    // Handle file selection
    const handleFileChange = (e) => {
        setSelectedFile(e.target.files[0]);
        setUploadStatus('');
        setStatusType('');
    };

    // Handle file upload
    const handleFileUpload = async() => {
        if (!selectedFile) {
            setStatusType('error');
            setUploadStatus('Please select a file to upload');
            return;
        }

        try {
            const formData = new FormData();
            formData.append('file', selectedFile);

            const response = await axios.post('/api/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });

            setStatusType('success');
            setUploadStatus(response.data.message || 'File uploaded successfully');
            setSelectedFile(null);
            fetchRecentUploads(); // Fetch recent uploads again after successful upload
        } catch (error) {
            console.error('Upload error:', error);
            const errorMessage =
                (error.response && error.response.data && error.response.data.message) ||
                'An error occurred during upload. Please ensure the file has the correct format and required columns.';
            setStatusType('error');
            setUploadStatus(`Error uploading file: ${String(errorMessage)}`);
        }
    };

    // Handle edit functionality
    const handleEdit = async(fileId) => {
        const file = recentUploads.find((upload) => upload.id === fileId);
        if (file) {
            try {
                const formData = new FormData();
                formData.append('file', selectedFile); // Assuming the user selects a new file for editing
                formData.append('fileId', fileId); // Send the file ID to update the correct record

                const response = await axios.put('/api/edit-upload', formData, {
                    headers: {
                        'Content-Type': 'multipart/form-data',
                    },
                });

                setStatusType('success');
                setUploadStatus(response.data.message || 'File edited successfully');
                fetchRecentUploads(); // Refresh the list after editing
            } catch (error) {
                console.error('Edit error:', error);
                setStatusType('error');
                setUploadStatus('Error editing file');
            }
        }
    };

    // Handle delete functionality
    const handleDelete = async(fileId) => {
        try {
            const response = await axios.delete(`/api/delete-upload/${fileId}`); // Backend endpoint to delete the file
            setStatusType('success');
            setUploadStatus(response.data.message || 'File deleted successfully');
            fetchRecentUploads(); // Refresh the list after deletion
        } catch (error) {
            console.error('Delete error:', error);
            setStatusType('error');
            setUploadStatus('Error deleting file');
        }
    };

    return ( <
        div className = "upload-container" >
        <
        h1 > File Upload < /h1> <
        span >
        <
        strong className = "red-text" > NOTE: < /strong> The file you upload here will be added to the database. The file format should be '.CSV' or '.JSON'. <
        /span> <
        p >
        <
        strong > Required columns: < /strong>  <
        span className = "red-text" >
        Name, Reg_No, Year, School, Company, <
        span className = "blue-text" > ApplicationStatus(Applied, Shortlisted, Placed, Not placed) < /span>,  <
        span className = "blue-text" > Category(Super dream internship, Super dream offer, Dream offer, Dream internship, Restricted dream offer) < /span>,  <
        span className = "blue-text" > CTC < /span> <
        /span> <
        /p>

        <
        input type = "file"
        onChange = { handleFileChange }
        accept = ".csv,.json" /
        >
        <
        button className = "upload-button"
        onClick = { handleFileUpload }
        disabled = {!selectedFile } >
        Upload <
        /button>

        {
            uploadStatus && ( <
                p className = { statusType === 'success' ? 'success-message' : 'error-message' } > { uploadStatus } <
                /p>
            )
        }

        { /* Recent Uploads Section */ } <
        h2 > Recent Uploads < /h2> <
        div >
        <
        label > Filter by Date: < /label> <
        input type = "date"
        value = { selectedDateFilter }
        onChange = {
            (e) => setSelectedDateFilter(e.target.value) }
        /> <
        /div>

        <
        ul > {
            recentUploads
            .filter((upload) => {
                if (!selectedDateFilter) return true; // No filter selected, show all
                return upload.date === selectedDateFilter; // Filter by date
            })
            .map((upload) => ( <
                li key = { upload.id } >
                <
                div >
                <
                strong > { upload.filename } < /strong> - {new Date(upload.date).toLocaleDateString()} <
                button onClick = {
                    () => handleEdit(upload.id) } > Edit < /button> <
                button onClick = {
                    () => handleDelete(upload.id) } > Delete < /button> <
                /div> <
                /li>
            ))
        } <
        /ul> <
        /div>
    );
};

export default UploadPage;
